import React, { useEffect, useState, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";

// Gamified Survey — cinematic page-change scroller with dramatic animations
// Tailwind + Framer Motion required.

// ====== CONFIG: set your opening video path here ======
const OPENING_VIDEO_SRC = '/opening.mp4'; // <-- put your video in public/ and set this path
// ======================================================

const QUESTIONS = [
  { id: "intro", type: "stage", title: "Nightfall at HackPlay", subtitle: "Step into a neon-lit hall. Your choices tune the journey." },
  {
    id: "q1",
    type: "mcq",
    title: "Entrance — choose your mindset",
    choices: ["Build something cool", "Learn fast", "Meet people", "Chase the prizes"],
    points: { "Build something cool": 20, "Learn fast": 10, "Meet people": 15, "Chase the prizes": 5 },
    flavor: "The doors close. A low bass. Pick the vibe that follows you.",
  },
  {
    id: "q2",
    type: "rating",
    title: "Charge Meter — how hyped are you?",
    scale: 5,
    pointsPerStar: 6,
    flavor: "The stage lights react to your energy.",
  },
  {
    id: "q3",
    type: "text",
    title: "One-line pitch — what's the idea?",
    maxLength: 140,
    points: 15,
    flavor: "A single line — the fragment your team will build around.",
  },
  {
    id: "q4",
    type: "minigame",
    title: "Charge the core — tap to power your idea",
    timeLimit: 6000,
    pointsOnSuccess: 30,
    flavor: "Fast reflexes prove prototype-ready energy.",
  },
  {
    id: "q5",
    type: "mcq",
    title: "Team vibe — pick your tribe",
    choices: ["Chill coders", "All-nighters", "Balanced squad", "Solo hero"],
    points: { "Chill coders": 10, "All-nighters": 20, "Balanced squad": 15, "Solo hero": 5 },
    flavor: "Who will you ride the night with?",
    dependsOn: { q1: "Build something cool" },
  },
  { id: "end", type: "stage", title: "Curtain Call", subtitle: "Collect badges, revisit your pitch, and step back into the light." },
];

function lerp(a, b, n) { return (1 - n) * a + n * b; }

function confettiBurst(canvas) {
  if (!canvas) return;
  const ctx = canvas.getContext("2d");
  const W = (canvas.width = window.innerWidth);
  const H = (canvas.height = window.innerHeight);
  const pieces = [];
  for (let i = 0; i < 80; i++) pieces.push({ x: W/2, y: H/4, vx: (Math.random()-0.5)*12, vy: (Math.random()-1.5)*10, r: Math.random()*6+3, color: ["#ff6b6b","#ffd93d","#6bcB77","#6b8aff","#ff85e3"][Math.floor(Math.random()*5)], rot: Math.random()*360, vr: Math.random()*8-4 });
  let t = 0;
  function frame() {
    t++;
    ctx.clearRect(0,0,W,H);
    for (let p of pieces) {
      p.x += p.vx;
      p.y += p.vy + 0.3;
      p.vy += 0.06;
      ctx.save();
      ctx.translate(p.x, p.y);
      ctx.rotate((p.rot * Math.PI)/180);
      ctx.fillStyle = p.color;
      ctx.fillRect(-p.r/2, -p.r/2, p.r, p.r*0.6);
      ctx.restore();
    }
    if (t < 180) requestAnimationFrame(frame); else ctx.clearRect(0,0,W,H);
  }
  frame();
}

// VideoBackground: plays the video behind the scene (video lives under bricks)
// Handles its own readiness events and calls onReady() when it's playable.
function VideoBackground({ src, videoRef, onReady }) {
  const internalRef = useRef(null);
  const ref = videoRef || internalRef;

  useEffect(() => {
    const v = ref.current;
    if (!v) return;
    v.muted = true;
    v.playsInline = true;
    // try to play (muted allows autoplay on most browsers)
    const p = v.play && v.play(); if (p && p.catch) p.catch(() => {});

    let fired = false;
    function markReady() {
      if (fired) return; fired = true; onReady && onReady();
    }

    function onCanPlay() { markReady(); }
    v.addEventListener('canplaythrough', onCanPlay);
    v.addEventListener('loadedmetadata', onCanPlay);

    // allow user to click/tap the background video to force-ready (skip loader)
    function onClick() { markReady(); }
    v.addEventListener('click', onClick);

    // safety timeout in case events don't fire
    const safety = setTimeout(() => markReady(), 3000);

    return () => { v.removeEventListener('canplaythrough', onCanPlay); v.removeEventListener('loadedmetadata', onCanPlay); v.removeEventListener('click', onClick); clearTimeout(safety); };
  }, [src, onReady, ref]);

  return (
    <video
      ref={ref}
      src={src}
      muted
      loop
      playsInline
      autoPlay
      preload="metadata"
      style={{ position: 'fixed', inset: 0, width: '100%', height: '100%', objectFit: 'cover', zIndex: -50, pointerEvents: 'auto', opacity: 1 }}
    />
  );
}

// LoadingOverlay: shows a slick progress bar before the UI reveals (short & smooth)
function LoadingOverlay({ progress }) {
  // progress: 0..100
  return (
    <div className="fixed inset-0 flex items-center justify-center" style={{ zIndex: 99999, pointerEvents: 'none' }}>
      <div className="w-full max-w-2xl px-6">
        <div className="mb-4 text-center text-white/90 font-medium select-none">Loading experience</div>
        <div className="w-full h-3 bg-gray-800 rounded-full overflow-hidden shadow-inner">
          <div className="h-full rounded-full" style={{ width: `${progress}%`, background: 'linear-gradient(90deg,#7c3aed,#06b6d4)', transition: 'width 220ms linear' }} />
        </div>
        <div className="mt-2 text-xs text-center text-gray-300 select-none">{Math.round(progress)}%</div>
      </div>
    </div>
  );
}

// --- New custom cursor (kept) ---
function CustomCursor() {
  const dotRef = useRef(null);
  const ringRef = useRef(null);
  const pos = useRef({ x: typeof window !== 'undefined' ? window.innerWidth/2 : 0, y: typeof window !== 'undefined' ? window.innerHeight/2 : 0 });
  const hover = useRef(false);
  const rafRef = useRef(null);

  useEffect(() => {
    const dot = dotRef.current; const ring = ringRef.current;
    if(!dot || !ring) return;

    function onMouseMove(e){ pos.current.x = e.clientX; pos.current.y = e.clientY; }
    function onHoverChange(e){ const t = e.target; const should = !!t.closest && (t.closest('button, a, [role="button"], input, textarea') != null); hover.current = should; }

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseover', onHoverChange);

    let cx = pos.current.x; let cy = pos.current.y; let ringScale = 1;
    function loop(){
      cx = lerp(cx, pos.current.x, 0.18);
      cy = lerp(cy, pos.current.y, 0.18);
      dot.style.transform = `translate3d(${cx-6}px, ${cy-6}px, 0)`;
      const rx = lerp(parseFloat(ring.dataset.x || cx), cx, 0.08);
      const ry = lerp(parseFloat(ring.dataset.y || cy), cy, 0.08);
      ring.dataset.x = rx; ring.dataset.y = ry;
      ringScale = lerp(ringScale, hover.current ? 1.9 : 1, 0.12);
      ring.style.transform = `translate3d(${rx-18}px, ${ry-18}px, 0) scale(${ringScale})`;
      ring.style.opacity = 0.9;
      rafRef.current = requestAnimationFrame(loop);
    }
    rafRef.current = requestAnimationFrame(loop);

    return () => { window.removeEventListener('mousemove', onMouseMove); window.removeEventListener('mouseover', onHoverChange); cancelAnimationFrame(rafRef.current); };
  }, []);

  useEffect(() => {
    document.documentElement.classList.add('custom-cursor-hide');
    return () => document.documentElement.classList.remove('custom-cursor-hide');
  }, []);

  return (
    <>
      <div ref={ringRef} style={{ position: 'fixed', left: 0, top: 0, width: 36, height: 36, borderRadius: 9999, border: '1.5px solid rgba(255,255,255,0.12)', pointerEvents: 'none', transform: 'translate3d(0,0,0)', transition: 'background-color 160ms, border-color 160ms', zIndex: 100000 }} />
      <div ref={dotRef} style={{ position: 'fixed', left: 0, top: 0, width: 12, height: 12, borderRadius: 9999, background: 'radial-gradient(circle at 30% 30%, #fff, #ddd)', boxShadow: '0 6px 20px rgba(125,72,255,0.14)', pointerEvents: 'none', transform: 'translate3d(0,0,0)', zIndex: 100001 }} />
      <style>{`.custom-cursor-hide, .custom-cursor-hide * { cursor: none !important; }`}</style>
    </>
  );
}

function MiniGame({ timeLimit = 6000, onComplete, flavor }) {
  const [charge, setCharge] = useState(0);
  const runningRef = useRef(false);
  const raf = useRef(null);
  const startedAt = useRef(0);
  const manualBoost = useRef(0);

  useEffect(() => () => cancelAnimationFrame(raf.current), []);

  function start() { if (runningRef.current) return; runningRef.current = true; manualBoost.current = 0; startedAt.current = performance.now(); tick(); }
  function tick() {
    raf.current = requestAnimationFrame(() => {
      const elapsed = performance.now() - startedAt.current;
      const base = Math.min(100, Math.round((elapsed / timeLimit) * 100));
      const total = Math.min(100, base + manualBoost.current);
      setCharge(total);
      if (total >= 100) { runningRef.current = false; cancelAnimationFrame(raf.current); onComplete(true); return; }
      if (elapsed >= timeLimit) { runningRef.current = false; cancelAnimationFrame(raf.current); onComplete(total >= 60); return; }
      tick();
    });
  }
  function handleTap() { if (!runningRef.current) start(); else { manualBoost.current = Math.min(100, manualBoost.current + 12); setCharge(c => Math.min(100, c + 12)); } }

  return (
    <div className="w-full flex flex-col items-center gap-4">
      <div className="text-sm text-gray-400 italic">{flavor}</div>
      <div className="relative w-56 h-56 flex items-center justify-center">
        <motion.div onClick={handleTap} whileTap={{scale:0.9}} whileHover={{scale:1.04}} transition={{type:'spring', stiffness:300}} className="w-40 h-40 rounded-full bg-gradient-to-tr from-indigo-500 to-pink-500 shadow-2xl flex items-center justify-center cursor-pointer relative overflow-hidden">
          <motion.div animate={{rotate:[0,360]}} transition={{repeat: Infinity, duration:8, ease:'linear'}} className="absolute inset-0 opacity-20" />
          <div className="text-white font-extrabold text-xl">TAP</div>
        </motion.div>
      </div>
      <div className="w-full max-w-xl">
        <div className="text-sm mb-2 text-gray-300">Charge: {charge}%</div>
        <div className="w-full h-4 bg-gray-800 rounded-full overflow-hidden border border-gray-700">
          <motion.div className="h-full rounded-full" style={{background: 'linear-gradient(90deg,#7c3aed,#06b6d4)'}} animate={{width: `${charge}%`}} transition={{type:'spring', stiffness:140, damping:16}} />
        </div>
      </div>
    </div>
  );
}

export default function GamifiedSurveyForm() {
  const [answers, setAnswers] = useState(() => { try { const raw = localStorage.getItem('gamifiedSurveyAnswers'); return raw ? JSON.parse(raw) : {}; } catch (e) { return {}; } });
  const [score, setScore] = useState(() => Number(localStorage.getItem('gamifiedSurveyScore')) || 0);
  const [showFinish, setShowFinish] = useState(false);
  const [openingDone, setOpeningDone] = useState(false);
  const [showResponses, setShowResponses] = useState(false);
  const canvasRef = useRef(null);
  const bgCanvasRef = useRef(null);
  const videoRef = useRef(null);

  useEffect(() => localStorage.setItem('gamifiedSurveyAnswers', JSON.stringify(answers)), [answers]);
  useEffect(() => localStorage.setItem('gamifiedSurveyScore', String(score)), [score]);

  const visible = QUESTIONS.filter(q => { if (!q.dependsOn) return true; const keys = Object.keys(q.dependsOn); return keys.every(k => answers[k] === q.dependsOn[k]); });

  const containerRef = useRef(null);
  const chapterRefs = useRef([]);
  chapterRefs.current = [];
  const addChapterRef = (el) => { if (el) chapterRefs.current.push(el); };

  const [height, setHeight] = useState(typeof window !== 'undefined' ? window.innerHeight : 800);
  useEffect(() => { function onResize(){ setHeight(window.innerHeight); } window.addEventListener('resize', onResize); return () => window.removeEventListener('resize', onResize); }, []);

  useEffect(() => {
    const prev = { overflow: document.documentElement.style.overflow, touchAction: document.body.style.touchAction };
    document.documentElement.style.overflow = 'hidden';
    document.body.style.touchAction = 'none';
    return () => { document.documentElement.style.overflow = prev.overflow; document.body.style.touchAction = prev.touchAction; };
  }, []);

  const targetY = useRef(0);
  const currentY = useRef(0);
  const rafRef = useRef(null);
  const lastWheelAt = useRef(0);
  const isScrolling = useRef(false);

  const SNAP_DELAY = 70; // tuned
  const LERP_FAST = 0.42;
  const LERP_SNAP = 0.62;

  const cursor = useRef({ x: typeof window !== 'undefined' ? window.innerWidth/2 : 0, y: typeof window !== 'undefined' ? window.innerHeight/2 : 0, active: false });
  const lastCursorUpdate = useRef(0);
  const bgRAF = useRef(null);

  const activeIndexRef = useRef(0);
  const [activeIndex, setActiveIndex] = useState(0);

  // Loading state: small simulated progress that is tied to video readiness
  const [loadProgress, setLoadProgress] = useState(0);
  const minLoadingMs = 700; // ensure the bar is visible briefly for polish

  useEffect(() => {
    let raf = null; let start = performance.now(); let simulated = 0;
    function tick() {
      const now = performance.now();
      const elapsed = now - start;
      // progress eases toward 92% while not ready; final jump to 100 when ready
      simulated = Math.min(92, simulated + (100 - simulated) * 0.06 + 0.8);
      setLoadProgress(prev => Math.min(92, Math.max(prev, simulated)));
      raf = requestAnimationFrame(tick);
    }
    raf = requestAnimationFrame(tick);

    return () => cancelAnimationFrame(raf);
  }, []);

  // When video background reports it's ready, finish progress and reveal UI
  function handleVideoReady() {
    setLoadProgress(100);
    setTimeout(() => setOpeningDone(true), 280);
  }

  useEffect(() => {
    const isTouching = { v: false };
    const touchStartY = { v: 0 };

    function clampTarget(){ const max = Math.max(0, (visible.length - 1) * height); if (targetY.current < 0) targetY.current = 0; if (targetY.current > max) targetY.current = max; }

    function onWheel(e){ e.preventDefault(); targetY.current += e.deltaY * 1.2; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; }
    function onTouchStart(e){ touchStartY.v = e.touches[0].clientY; isTouching.v = true; }
    function onTouchMove(e){ if(!isTouching.v) return; const y = e.touches[0].clientY; const delta = touchStartY.v - y; targetY.current += delta; touchStartY.v = y; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; }
    function onTouchEnd(){ isTouching.v = false; }
    function onKey(e){ if(e.key === 'ArrowDown'){ targetY.current += height * 0.95; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; } if(e.key === 'ArrowUp'){ targetY.current -= height * 0.95; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; } }

    const el = containerRef.current || window;
    el.addEventListener('wheel', onWheel, { passive: false });
    window.addEventListener('touchstart', onTouchStart, { passive: false });
    window.addEventListener('touchmove', onTouchMove, { passive: false });
    window.addEventListener('touchend', onTouchEnd, { passive: false });
    window.addEventListener('keydown', onKey);

    function onPointer(e){ const now = performance.now(); if (now - lastCursorUpdate.current > 12) { cursor.current.x = e.clientX; cursor.current.y = e.clientY; cursor.current.active = true; lastCursorUpdate.current = now; } }
    function onPointerLeave(){ cursor.current.active = false; }
    function onPointerDown(e){ const px = e.clientX; const py = e.clientY; cursor.current.x = px; cursor.current.y = py; if (bgCanvasRef.current && bgCanvasRef.current._triggerPop) bgCanvasRef.current._triggerPop(px, py); }

    window.addEventListener('pointermove', onPointer);
    window.addEventListener('pointerout', onPointerLeave);
    window.addEventListener('pointerdown', onPointerDown);

    return () => { el.removeEventListener('wheel', onWheel); window.removeEventListener('touchstart', onTouchStart); window.removeEventListener('touchmove', onTouchMove); window.removeEventListener('touchend', onTouchEnd); window.removeEventListener('keydown', onKey); window.removeEventListener('pointermove', onPointer); window.removeEventListener('pointerout', onPointerLeave); window.removeEventListener('pointerdown', onPointerDown); };
  }, [height, visible.length]);

  useEffect(() => {
    const canvas = bgCanvasRef.current; if(!canvas) return; const ctx = canvas.getContext('2d'); let W = canvas.width = window.innerWidth; let H = canvas.height = window.innerHeight; const DPR = Math.min(window.devicePixelRatio || 1, 1.5); canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = W + 'px'; canvas.style.height = H + 'px'; ctx.scale(DPR, DPR);

    // adaptive brick sizing for performance
    let BRICK_W = window.innerWidth < 640 ? 120 : 96; let BRICK_H = window.innerWidth < 640 ? 56 : 44; let cols = Math.ceil(W / BRICK_W); let rows = Math.ceil(H / BRICK_H);
    const MAX_BRICKS = 900; // reduce to improve perf
    if (cols * rows > MAX_BRICKS) {
      const scaleUp = Math.sqrt((cols * rows) / MAX_BRICKS);
      BRICK_W = Math.ceil(BRICK_W * scaleUp);
      BRICK_H = Math.ceil(BRICK_H * scaleUp);
      cols = Math.ceil(W / BRICK_W); rows = Math.ceil(H / BRICK_H);
    }

    const bricks = [];
    for (let r = 0; r < rows; r++) {
      for (let c = 0; c < cols; c++) {
        const x = c * BRICK_W + (r % 2 ? BRICK_W / 2 : 0);
        const y = r * BRICK_H;
        const cx = x + BRICK_W/2;
        const cy = y + BRICK_H/2;
        bricks.push({ x, y, w: BRICK_W, h: BRICK_H, baseX: x, baseY: y, cx, cy, pop: 0, vy: 0, phase: Math.random() * Math.PI * 2, falling: false, fallY: 0, fallVy: 0, opacity: 1, gone: false });
      }
    }

    canvas._triggerPop = function(px, py) {
      // find nearest brick to click (not exact grid check) for nicer feel
      let nearest = null; let best = Infinity;
      for (let b of bricks) {
        if (b.gone) continue;
        const dx = b.cx - px; const dy = b.cy - py; const d = Math.sqrt(dx*dx + dy*dy);
        if (d < best) { best = d; nearest = b; }
      }
      if (nearest && !nearest.falling && best < Math.max(120, Math.min(window.innerWidth, window.innerHeight) * 0.06)) {
        nearest.falling = true; nearest.fallVy = Math.random() * 4 + 2; nearest.fallY = 0;
      }
      const R = 120;
      for (let b of bricks) {
        if (b.falling || b.gone) continue;
        const dx = b.cx - px; const dy = b.cy - py; const d = Math.sqrt(dx*dx + dy*dy);
        if (d < R) {
          const norm = 1 - d / R;
          b.pop = Math.max(b.pop, 0.3 * norm);
          b.vy += (4 * norm) + (Math.random() * 2);
        }
      }
    };

    let lastT = performance.now();
    function draw(now) {
      let dt = Math.min(40, now - lastT) * 0.001; if (dt <= 0) { bgRAF.current = requestAnimationFrame(draw); return; } lastT = now;
      ctx.clearRect(0,0,W,H);
      // when video background is active we paint a translucent wash so the video reads through
      ctx.fillStyle = openingDone ? 'rgba(3,3,10,0.42)' : '#03030a'; ctx.fillRect(0,0,W,H);

      const maxD = Math.max(W, H) * 0.65;
      const time = now * 0.001;

      const skipShadows = bricks.length > 700; // skip heavy shadows on large grids

      for (let b of bricks) {
        if (b.gone) continue;
        if (b.falling) {
          b.fallVy += 900 * dt; b.fallY += b.fallVy * dt; b.opacity = Math.max(0, 1 - (b.fallY / (H * 1.5)));
          if (b.fallY > H + 100) { b.gone = true; continue; }
        }
        const dx = b.cx - cursor.current.x; const dy = b.cy - cursor.current.y; const d = Math.sqrt(dx*dx + dy*dy);
        let t = Math.max(0, 1 - d / maxD); t = Math.pow(t, 0.9);
        const lift = b.falling ? 0 : Math.min(14, 10 * t + b.pop * 18);
        const wobble = b.falling ? 0 : Math.sin(b.phase + time * 1.2) * 3 * t;
        if (!b.falling) { b.vy -= 30 * dt; b.vy *= (0.92 - Math.min(0.18, t * 0.12)); b.pop = Math.max(0, b.pop - 0.012); }
        const yOffset = b.falling ? b.fallY : (-lift + b.vy * dt * 60);
        const baseR = 8 + Math.round(40 * t + 10 * Math.sin(b.phase + time * 0.6));
        const baseG = 12 + Math.round(18 * t + 8 * Math.cos(b.phase + time * 0.8));
        const baseB = 18 + Math.round(24 * t + 6 * Math.sin(b.phase + time * 0.7));
        ctx.save(); ctx.globalAlpha = b.opacity; ctx.fillStyle = `rgb(${baseR}, ${baseG}, ${baseB})`;
        if (lift > 2 && !b.falling && !skipShadows) { ctx.shadowBlur = Math.min(28, lift * 1.6); ctx.shadowColor = `rgba(40,60,160,${Math.min(0.22, 0.06 * t + 0.06 * b.pop)})`; } else ctx.shadowBlur = 0;
        const px = b.baseX + wobble * 0.6; const py = b.baseY + yOffset;
        roundRect(ctx, px + 6, py + 6, b.w - 12, b.h - 12, 6); ctx.fill();
        if ((t > 0.08 || b.pop > 0.02) && !b.falling) { ctx.shadowBlur = 0; ctx.fillStyle = `rgba(255,255,255,${0.03 * t + 0.06 * b.pop})`; ctx.fillRect(px + 12, py + 10, b.w - 24, Math.max(3, Math.min(8, 4 * t + 6 * b.pop))); }
        ctx.restore();
      }

      bgRAF.current = requestAnimationFrame(draw);
    }

    function roundRect(ctx, x, y, w, h, r) { const minr = Math.min(r, w/2, h/2); ctx.beginPath(); ctx.moveTo(x + minr, y); ctx.arcTo(x + w, y, x + w, y + h, minr); ctx.arcTo(x + w, y + h, x, y + h, minr); ctx.arcTo(x, y + h, x, y, minr); ctx.arcTo(x, y, x + w, y, minr); ctx.closePath(); }

    function onResize(){ W = canvas.width = window.innerWidth; H = canvas.height = window.innerHeight; canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = W + 'px'; canvas.style.height = H + 'px'; ctx.scale(DPR, DPR); }
    window.addEventListener('resize', onResize);
    bgRAF.current = requestAnimationFrame(draw);

    return () => { window.removeEventListener('resize', onResize); cancelAnimationFrame(bgRAF.current); };
  }, [openingDone]); // wait until opening done to start heavy canvas

  useEffect(() => {
    function frame(now){
      currentY.current = lerp(currentY.current, targetY.current, (performance.now() - lastWheelAt.current > SNAP_DELAY) ? LERP_SNAP : LERP_FAST);
      const el = containerRef.current; if(el) el.style.transform = `translate3d(0, ${-currentY.current}px, 0)`;

      chapterRefs.current.forEach((node, i) => {
        if(!node) return; const center = i * height; const offset = (currentY.current - center) / height; const abs = Math.abs(offset);
        const translateY = offset * 34; const translateX = 0; const rotate = offset * 3.6; const scale = 1 - Math.min(0.08, abs * 0.08); const opacity = 1 - Math.min(0.9, abs * 0.9);
        node.style.transform = `translate3d(${translateX}px, ${translateY}px, 0) rotate(${rotate}deg) scale(${scale})`;
        node.style.opacity = String(opacity);
      });

      const idx = Math.round(currentY.current / height);
      if(activeIndexRef.current !== idx){ activeIndexRef.current = idx; setActiveIndex(idx); }
      rafRef.current = requestAnimationFrame(frame);
    }
    rafRef.current = requestAnimationFrame(frame);
    return () => cancelAnimationFrame(rafRef.current);
  }, [height, visible.length]);

  function scrollToIndex(i){ const max = Math.max(0, (visible.length - 1) * height); const dest = Math.max(0, Math.min(i * height, max)); targetY.current = dest; lastWheelAt.current = performance.now(); isScrolling.current = true; }

  const AUTO_ADVANCE_DELAY_MS = 240; const MINIGAME_ADVANCE_MS = 300;

  function handleAnswer(qid, value){ setAnswers(prev => ({ ...prev, [qid]: value })); const questionDef = QUESTIONS.find(x => x.id === qid) || {}; let gained = 0; if(questionDef.type === 'mcq' && questionDef.points) gained = questionDef.points[value] || 0; else if(questionDef.type === 'rating') gained = value * (questionDef.pointsPerStar || 1); else if(questionDef.type === 'text') gained = questionDef.points || 0; if(gained){ setScore(s => s + gained); pulseScore(); } if(questionDef.type === 'mcq' || questionDef.type === 'rating'){ const next = Math.min(activeIndexRef.current + 1, visible.length - 1); setTimeout(() => scrollToIndex(next), AUTO_ADVANCE_DELAY_MS); } }

  function pulseScore(){ const el = document.getElementById('score-badge'); if(!el) return; el.animate([{ transform: 'translateY(0) scale(1)' }, { transform: 'translateY(-6px) scale(1.06)' }, { transform: 'translateY(0) scale(1)' }], { duration: 520, easing: 'ease-out' }); }

  function onMiniGameComplete(success){ if(success){ setScore(s => s + 30); pulseScore(); } setAnswers(prev => ({ ...prev, q4: success ? 'success' : 'fail' })); setTimeout(() => scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)), MINIGAME_ADVANCE_MS); }

  function finish(){ setShowFinish(true); if(canvasRef.current) confettiBurst(canvasRef.current); }

  function resetAll(){ if(!window.confirm('Reset survey? This clears local progress.')) return; setAnswers({}); setScore(0); setShowFinish(false); targetY.current = 0; currentY.current = 0; if(containerRef.current) containerRef.current.style.transform = 'translate3d(0,0,0)'; }

  function ChapterContent({ item, idx }){
    // stage page (intro / end)
    if(item.type === 'stage'){
      const isFirst = idx === 0;
      const isLast = idx === Math.max(0, visible.length - 1);
      return (
        <div className="text-center max-w-3xl mx-auto">
          <motion.h1 initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.8 }} className="text-5xl md:text-7xl font-extrabold tracking-tight bg-clip-text text-transparent" style={{ backgroundImage: 'linear-gradient(90deg,#8b5cf6,#06b6d4)' }}>{item.title}</motion.h1>
          {item.subtitle && <motion.p initial={{ y:20, opacity:0 }} animate={{ y:0, opacity:0.95 }} transition={{ delay:0.15 }} className="mt-6 text-lg text-gray-300">{item.subtitle}</motion.p>}

          {/* richer stage card */}
          <div className="mt-8 mx-auto w-full max-w-md bg-gradient-to-b from-black/60 to-transparent border border-gray-800 rounded-2xl p-6 shadow-xl">
            <div className="flex items-center gap-3">
              <svg width="40" height="40" viewBox="0 0 24 24" fill="none" className="opacity-95"><path d="M12 2l3 7h7l-5.5 4 2 7L12 17l-6.5 3 2-7L2 9h7l3-7z" stroke="#c4b5fd" strokeWidth="0.8"/></svg>
              <div className="text-left">
                <div className="font-semibold text-lg">Experience</div>
                <div className="text-sm text-gray-400">A cinematic flow — read the prompt, pick quickly, and earn points.</div>
              </div>
            </div>

            <div className="mt-6">
              {/* First page: only Enter. Middle pages: Enter + Skip. Last page: Restart / View responses */}
              {isLast ? (
                <div className="flex gap-3 justify-center">
                  <button onClick={() => resetAll()} className="px-5 py-3 rounded-lg bg-gradient-to-r from-red-600 to-pink-500 text-white">Restart</button>
                  <button onClick={() => setShowResponses(true)} className="px-5 py-3 rounded-lg bg-gray-800 text-gray-300 border border-gray-700">View responses</button>
                </div>
              ) : (
                <div className="flex gap-3 justify-center">
                  <button onClick={() => scrollToIndex(Math.min(idx + 1, visible.length - 1))} className="px-5 py-3 rounded-lg bg-gradient-to-r from-indigo-600 to-cyan-500 text-white">Enter</button>
                  {/* render Skip only when not first or last */}
                  {!isFirst && <button onClick={() => finish()} className="px-5 py-3 rounded-lg bg-gray-800 text-gray-300 border border-gray-700">Skip</button>}
                </div>
              )}
            </div>
          </div>
        </div>
      );
    }

    if(item.type === 'mcq'){
      return (
        <div className="max-w-3xl w-full mx-auto">
          <div className="text-sm text-gray-400 italic mb-4">{item.flavor}</div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">{item.choices.map(c => (
            <motion.button key={c} whileHover={{ scale: 1.04 }} onClick={() => handleAnswer(item.id, c)} className={`py-7 px-6 rounded-3xl border ${answers[item.id] === c ? 'bg-gradient-to-r from-indigo-600 to-cyan-500 text-white shadow-2xl transform-gpu' : 'bg-gray-900 text-gray-300 border-gray-800'}`}>
              <div className="font-semibold text-xl">{c}</div>
              <div className="text-sm text-gray-400 mt-1">{item.points && item.points[c] ? `${item.points[c]} pts` : ''}</div>
            </motion.button>
          ))}</div>
        </div>
      );
    }

    if(item.type === 'rating'){
      return (
        <div className="text-center">
          <div className="text-sm text-gray-400 italic mb-4">{item.flavor}</div>
          <div className="flex items-center justify-center gap-4">{Array.from({ length: item.scale }).map((_, i) => (
            <motion.button key={i} onClick={() => handleAnswer(item.id, i+1)} whileTap={{ scale: 0.95 }} className={`w-16 h-16 rounded-full flex items-center justify-center ${answers[item.id] === i+1 ? 'bg-yellow-400 text-black' : 'bg-gray-800 text-gray-300'}`}>{i+1}</motion.button>
          ))}</div>
        </div>
      );
    }

    if(item.type === 'text'){
      return (
        <div className="w-full max-w-3xl mx-auto">
          <div className="text-sm text-gray-400 italic mb-4">{item.flavor}</div>
          <textarea maxLength={item.maxLength} rows={5} value={answers[item.id] || ''} onChange={(e) => setAnswers(prev => ({ ...prev, [item.id]: e.target.value }))} className="w-full p-4 rounded-xl border bg-gray-900 text-gray-100 border-gray-700" placeholder="One-line pitch..." />
          <div className="text-xs text-gray-400 mt-2">{(answers[item.id] || '').length}/{item.maxLength}</div>
          <div className="mt-4 flex gap-4 justify-center">
            <button onClick={() => { if(item.points && (answers[item.id] || '').length > 0) { setScore(s => s + item.points); pulseScore(); } scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)); }} className="px-6 py-3 rounded-lg bg-gradient-to-r from-indigo-600 to-cyan-500 text-white font-semibold">Submit</button>
            <button onClick={() => setAnswers(prev => ({ ...prev, [item.id]: '' }))} className="px-6 py-3 rounded-lg bg-gray-800 text-gray-300 border border-gray-700 font-semibold">Clear</button>
          </div>
        </div>
      );
    }

    if(item.type === 'minigame') return <MiniGame timeLimit={item.timeLimit} onComplete={onMiniGameComplete} flavor={item.flavor} />;
    return null;
  }

  return (
    <div className="min-h-screen bg-black text-gray-100 relative overflow-hidden">
      <style>{`.cinema-stack { will-change: transform; } .nav-dot { width:12px; height:12px; border-radius:9999px; } .chapter-node { will-change: transform, opacity; } .responses-pre { max-height:60vh; overflow:auto; white-space:pre-wrap; font-family: ui-monospace, SFMono-Regular, Menlo, Monaco, monospace; }`}</style>

      <CustomCursor />

      {/* subtle gradient overlay on top of video */}
      <div className="fixed inset-0 -z-31 bg-gradient-to-b from-transparent to-black/80" />

      {/* Video background always mounted behind the bricks (zIndex -25). Bricks canvas will paint a translucent wash when openingDone is true so the video shows through. */}
      <VideoBackground src={OPENING_VIDEO_SRC} videoRef={videoRef} onReady={handleVideoReady} />

      {/* If not opened yet show a quick loading bar overlay tied to video readiness */}
      {!openingDone && <LoadingOverlay progress={loadProgress} />}

      <canvas ref={bgCanvasRef} className="pointer-events-none fixed inset-0 z-0" />
      <canvas ref={canvasRef} className="pointer-events-none fixed inset-0 z-50" />

      <div className="fixed right-6 top-6 z-60 flex flex-col gap-3 items-end">
        <div id="score-badge" className="px-4 py-2 bg-gradient-to-r from-purple-600 to-pink-500 text-white font-semibold rounded-full text-sm shadow-lg">{score} pts</div>
        <div className="text-xs text-gray-400">Level {Math.floor(score / 50) + 1}</div>
      </div>

      <nav className="fixed left-6 top-1/2 -translate-y-1/2 z-60 hidden md:flex flex-col gap-3">{visible.map((_, i) => (
        <button key={i} onClick={() => scrollToIndex(i)} className={`nav-dot ${i === activeIndex ? 'bg-gradient-to-r from-indigo-400 to-pink-400 shadow-2xl' : 'bg-gray-700/40'}`} aria-label={`Go to chapter ${i+1}`} />
      ))}</nav>

      <div ref={containerRef} className="cinema-stack fixed inset-0 top-0 left-0 w-full will-change-transform z-40">
        <div style={{ height: `${visible.length * height}px` }}>
          {visible.map((item, i) => (
            <section key={item.id} ref={addChapterRef} style={{ height: `${height}px` }} className="flex items-center justify-center px-6 md:px-12">
              <div className="w-full max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-6 items-center chapter-node">
                <aside className="col-span-1 hidden lg:flex flex-col gap-4 items-start">
                  <div className="text-sm text-gray-400">Chapter</div>
                  <motion.div animate={{ rotate: activeIndex === i ? 6 : 0 }} transition={{ type: 'spring' }} className="text-4xl md:text-5xl font-extrabold">{String(i+1).padStart(2,'0')}</motion.div>
                  <div className="w-1 h-full bg-gradient-to-b from-indigo-600 to-pink-500 rounded-full opacity-30" />
                </aside>
                <div className="col-span-2 bg-transparent rounded-xl p-8 text-center">
                  <motion.div initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }}>
                    <ChapterContent item={item} idx={i} />
                  </motion.div>
                </div>
              </div>
            </section>
          ))}
        </div>
      </div>

      {showFinish && (
        <div className="fixed inset-0 z-70 flex items-center justify-center">
          <div className="bg-black/70 backdrop-blur-sm rounded-xl p-8 text-center">
            <h2 className="text-4xl font-bold">All done 🎉</h2>
            <p className="mt-3 text-gray-300">You earned <span className="font-semibold">{score}</span> points. Thanks for playing!</p>
            <div className="mt-6 flex gap-3 justify-center"><button onClick={resetAll} className="px-4 py-2 rounded bg-red-700/20 text-red-300">Reset</button></div>
          </div>
        </div>
      )}

      <AnimatePresence>{showResponses && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-90 flex items-center justify-center p-6">
          <div className="bg-black/85 rounded-xl p-6 max-w-3xl w-full">
            <div className="flex justify-between items-center">
              <h3 className="text-xl font-semibold">Your Responses</h3>
              <button onClick={() => setShowResponses(false)} className="text-sm text-gray-400">Close</button>
            </div>
            <div className="mt-4 responses-pre text-sm text-gray-200 bg-gray-900 p-4 rounded">{JSON.stringify(answers, null, 2)}</div>
            <div className="mt-4 flex gap-3 justify-end">
              <button onClick={() => { setShowResponses(false); resetAll(); }} className="px-4 py-2 rounded bg-red-600 text-white">Restart</button>
            </div>
          </div>
        </motion.div>
      )}</AnimatePresence>
    </div>
  );
}
