import React, { useEffect, useState, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";

/**
 * src/App.js
 * Revised: chapter sidebar now has a grounded card background; buttons redesigned; survey UI given more creative interactions.
 * Theme: Neo-Noir / Electric Teal + Amber (updated visuals)
 * Requirements: React + framer-motion
 */

const THEME = {
  bg: "#07101A",
  panel: "rgba(6,10,16,0.78)",
  text: "#E6F8F5",
  muted: "#93A8B2",
  primary: "#00F5D4",
  accent: "#FFB86B",
  highlight: "#6E7BFF",
};

const MAX_VIBRATE_AMPLITUDE = 6; // px, tweak to taste

const QUESTIONS = [
  { id: "intro", type: "stage", title: "Nightfall at NeoPlay", subtitle: "Neon corridors, decisions that echo. Your journey starts now." },
  {
    id: "q1",
    type: "mcq",
    title: "Entrance — pick your intent",
    choices: ["Prototype a system", "Learn a trick", "Network & team up", "Hunt the prize"],
    points: { "Prototype a system": 20, "Learn a trick": 10, "Network & team up": 15, "Hunt the prize": 5 },
    flavor: "Doors hum open. Choose what follows you into the night.",
  },
  {
    id: "q2",
    type: "rating",
    title: "Charge Meter — how hyped are you?",
    scale: 5,
    pointsPerStar: 6,
    flavor: "Lights react to your energy level.",
  },
  {
    id: "q3",
    type: "text",
    title: "One-line pitch — what's the idea?",
    maxLength: 140,
    points: 15,
    flavor: "A single, sharp line — the seed to grow.",
  },
  {
    id: "q4",
    type: "minigame",
    title: "Core Charge — tap to energize",
    timeLimit: 6000,
    pointsOnSuccess: 30,
    flavor: "Fast taps power the prototype core.",
  },
  {
    id: "q5",
    type: "mcq",
    title: "Team vibe — pick your crew",
    choices: ["Late-night coders", "Design & chill", "Balanced squad", "Solo racer"],
    points: { "Late-night coders": 20, "Design & chill": 10, "Balanced squad": 15, "Solo racer": 5 },
    flavor: "Who will you carry the lamp with?",
    dependsOn: { q1: "Prototype a system" },
  },
  { id: "end", type: "stage", title: "Curtain Call", subtitle: "Badges collected, circuits cooled. Step into daylight." },
];

function lerp(a, b, n) { return (1 - n) * a + n * b; }
function hexToRgb(hex) {
  const h = hex.replace("#", "");
  return [parseInt(h.substring(0,2),16), parseInt(h.substring(2,4),16), parseInt(h.substring(4,6),16)];
}

/* ---------------- confetti ---------------- */
function confettiBurst(canvas) {
  if (!canvas) return;
  const ctx = canvas.getContext("2d");
  const W = (canvas.width = window.innerWidth);
  const H = (canvas.height = window.innerHeight);
  const palette = [THEME.primary, THEME.highlight, THEME.accent, "#FFD93D"];
  const pieces = Array.from({length:90}).map(() => ({
    x: W/2 + (Math.random()-0.5)*160,
    y: H/3 + (Math.random()-0.5)*90,
    vx: (Math.random()-0.5)*12,
    vy: (Math.random()-1.5)*10,
    r: Math.random()*6 + 3,
    color: palette[Math.floor(Math.random()*palette.length)],
    rot: Math.random()*Math.PI*2,
    vr: (Math.random()-0.5)*0.25,
    life: 130 + Math.random()*100,
  }));
  function frame(){
    ctx.clearRect(0,0,W,H);
    let alive = false;
    for (let p of pieces) {
      if (p.life > 0) {
        alive = true;
        p.vy += 0.24;
        p.x += p.vx; p.y += p.vy; p.rot += p.vr;
        ctx.save(); ctx.translate(p.x,p.y); ctx.rotate(p.rot); ctx.fillStyle = p.color; ctx.fillRect(-p.r/2, -p.r/2, p.r, p.r*0.6); ctx.restore();
        p.life--;
      }
    }
    if (alive) requestAnimationFrame(frame); else ctx.clearRect(0,0,W,H);
  }
  frame();
}

/* ---------------- Custom Cursor ---------------- */
function CustomCursor(){
  const dotRef = useRef(null), ringRef = useRef(null);
  const pos = useRef({ x: typeof window !== 'undefined' ? window.innerWidth/2 : 0, y: typeof window !== 'undefined' ? window.innerHeight/2 : 0 });
  const hover = useRef(false), rafRef = useRef(null);
  useEffect(()=>{
    const dot = dotRef.current, ring = ringRef.current; if(!dot || !ring) return;
    function mm(e){ pos.current.x = e.clientX; pos.current.y = e.clientY; }
    function hov(e){ const t = e.target; hover.current = !!(t.closest && t.closest('button, a, [role="button"], input, textarea')); }
    window.addEventListener('mousemove', mm); window.addEventListener('mouseover', hov);
    let cx = pos.current.x, cy = pos.current.y, scale = 1;
    function loop(){ cx = lerp(cx, pos.current.x, 0.16); cy = lerp(cy, pos.current.y, 0.16); dot.style.transform = `translate3d(${cx-6}px, ${cy-6}px, 0)`; const rx = lerp(parseFloat(ring.dataset.x||cx), cx, 0.08); const ry = lerp(parseFloat(ring.dataset.y||cy), cy, 0.08); ring.dataset.x = rx; ring.dataset.y = ry; scale = lerp(scale, hover.current?1.9:1, 0.12); ring.style.transform = `translate3d(${rx-18}px, ${ry-18}px, 0) scale(${scale})`; rafRef.current = requestAnimationFrame(loop); }
    rafRef.current = requestAnimationFrame(loop);
    return ()=>{ window.removeEventListener('mousemove', mm); window.removeEventListener('mouseover', hov); cancelAnimationFrame(rafRef.current); };
  }, []);
  useEffect(()=>{ document.documentElement.classList.add('custom-cursor-hide'); return ()=>document.documentElement.classList.remove('custom-cursor-hide'); }, []);
  return (
    <>
      <div ref={ringRef} style={{ position:'fixed', left:0, top:0, width:36, height:36, borderRadius:9999, border:`1.5px solid rgba(0,245,212,0.08)`, pointerEvents:'none', zIndex:100000 }} />
      <div ref={dotRef} style={{ position:'fixed', left:0, top:0, width:12, height:12, borderRadius:9999, background:'radial-gradient(circle at 30% 30%, #fff, #cfe)', boxShadow:'0 6px 30px rgba(0,245,212,0.08)', pointerEvents:'none', zIndex:100001 }} />
      <style>{`.custom-cursor-hide, .custom-cursor-hide * { cursor: none !important; }`}</style>
    </>
  );
}

/* ---------------- MiniGame ---------------- */
function MiniGame({ timeLimit = 6000, onComplete, flavor }){
  const [charge, setCharge] = useState(0); const runningRef = useRef(false); const raf = useRef(null); const startedAt = useRef(0); const manualBoost = useRef(0);
  useEffect(()=>() => cancelAnimationFrame(raf.current), []);
  function start(){ if(runningRef.current) return; runningRef.current = true; manualBoost.current = 0; startedAt.current = performance.now(); tick(); }
  function tick(){ raf.current = requestAnimationFrame(()=>{ const elapsed = performance.now() - startedAt.current; const base = Math.min(100, Math.round((elapsed/timeLimit)*100)); const total = Math.min(100, base + manualBoost.current); setCharge(total); if(total>=100){ runningRef.current=false; cancelAnimationFrame(raf.current); onComplete(true); return; } if(elapsed>=timeLimit){ runningRef.current=false; cancelAnimationFrame(raf.current); onComplete(total>=60); return; } tick(); }); }
  function handleTap(){ if(!runningRef.current) start(); else { manualBoost.current = Math.min(100, manualBoost.current + 12); setCharge(c=>Math.min(100,c+12)); } }
  return (
    <div style={{ width:'100%', display:'flex', flexDirection:'column', alignItems:'center', gap:12 }}>
      <div style={{ color: THEME.muted, fontSize:13 }}>{flavor}</div>
      <motion.div onClick={handleTap} whileTap={{ scale:0.92 }} whileHover={{ scale:1.03 }} transition={{ type:'spring', stiffness:300 }} style={{ width:140, height:140, borderRadius:999, display:'flex', alignItems:'center', justifyContent:'center', cursor:'pointer', background:`linear-gradient(135deg, ${THEME.primary}, ${THEME.highlight})`, boxShadow:'0 18px 48px rgba(0,245,212,0.06)' }}>
        <div style={{ color:'#02171A', fontWeight:900 }}>TAP</div>
      </motion.div>
      <div style={{ width:'100%', maxWidth:520 }}>
        <div style={{ color: THEME.muted, marginBottom:6 }}>Charge: {charge}%</div>
        <div style={{ height:12, width:'100%', borderRadius:10, overflow:'hidden', background:'rgba(255,255,255,0.02)' }}>
          <motion.div initial={{ width:0 }} animate={{ width: `${charge}%` }} transition={{ type:'spring', stiffness:140, damping:16 }} style={{ height:'100%', background:`linear-gradient(90deg, ${THEME.primary}, ${THEME.highlight})` }} />
        </div>
      </div>
    </div>
  );
}

/* ---------------- Main App ---------------- */
export default function GamifiedSurveyForm(){
  const [answers, setAnswers] = useState(()=>{ try{ const r = localStorage.getItem('gamifiedSurveyAnswers'); return r?JSON.parse(r):{} }catch{ return {}; } });
  const [score, setScore] = useState(()=>Number(localStorage.getItem('gamifiedSurveyScore'))||0);
  const [showFinish, setShowFinish] = useState(false);

  const canvasRef = useRef(null); // confetti
  const bgCanvasRef = useRef(null); // bricks

  const [height, setHeight] = useState(typeof window !== 'undefined' ? window.innerHeight : 800);
  useEffect(()=>{ function onResize(){ setHeight(window.innerHeight); } window.addEventListener('resize', onResize); return ()=>window.removeEventListener('resize', onResize); }, []);

  useEffect(()=>localStorage.setItem('gamifiedSurveyAnswers', JSON.stringify(answers)), [answers]);
  useEffect(()=>localStorage.setItem('gamifiedSurveyScore', String(score)), [score]);

  const visible = QUESTIONS.filter(q => { if(!q.dependsOn) return true; return Object.keys(q.dependsOn).every(k => answers[k] === q.dependsOn[k]); });

  // cinematic state
  const containerRef = useRef(null);
  const chapterRefs = useRef([]); chapterRefs.current = [];
  const addChapterRef = el => { if(el) chapterRefs.current.push(el); };

  const targetY = useRef(0); const currentY = useRef(0); const rafRef = useRef(null); const lastWheelAt = useRef(0);
  const SNAP_DELAY = 80; const LERP_FAST = 0.42; const LERP_SNAP = 0.62;

  const cursor = useRef({ x: typeof window !== 'undefined' ? window.innerWidth/2 : 0, y: typeof window !== 'undefined' ? window.innerHeight/2 : 0, active:false });
  const lastCursorUpdate = useRef(0); const bgRAF = useRef(null);

  const activeIndexRef = useRef(0); const [activeIndex, setActiveIndex] = useState(0); const activeSince = useRef(performance.now());

  const [pageIndex, setPageIndex] = useState(0); const pageIndexRef = useRef(0); const wheelAccum = useRef(0); const wheelLock = useRef(false);

  // loading intro animation: keep a small neon gate loader to land on page 0
  const [openingDone, setOpeningDone] = useState(false);
  const [loadingProgress, setLoadingProgress] = useState(0);
  useEffect(()=>{
    let raf = null; const start = performance.now(); const DURATION = 850; function ease(t){ return 1 - Math.pow(1 - t, 3); }
    function tick(now){ const t = Math.min(1, (now - start)/DURATION); setLoadingProgress(Math.round(ease(t)*100)); if(t<1) raf = requestAnimationFrame(tick); else { setTimeout(()=>{ setOpeningDone(true); pageIndexRef.current = 0; activeIndexRef.current = 0; targetY.current = 0; currentY.current = 0; if(containerRef.current) containerRef.current.style.transform = `translate3d(0,0,0)`; setPageIndex(0); setActiveIndex(0); activeSince.current = performance.now(); setLoadingProgress(100); }, 140); } }
    raf = requestAnimationFrame(tick); return ()=> cancelAnimationFrame(raf);
  }, []);

  // input handlers: wheel->page jumps, touch swipe, keyboard
  useEffect(()=>{
    const touchStart = { vY:0, time:0 };
    function clampPage(i){ return Math.max(0, Math.min(i, visible.length - 1)); }
    function setPage(i){ const c = clampPage(i); pageIndexRef.current = c; setPageIndex(c); targetY.current = c * height; lastWheelAt.current = performance.now(); activeIndexRef.current = c; setActiveIndex(c); activeSince.current = performance.now(); }

    function onWheel(e){ if(!openingDone) return; e.preventDefault(); if(wheelLock.current) return; wheelAccum.current += e.deltaY; const TH = 100; if(Math.abs(wheelAccum.current) >= TH){ const dir = wheelAccum.current > 0 ? 1 : -1; setPage(pageIndexRef.current + dir); wheelAccum.current = 0; wheelLock.current = true; setTimeout(()=> wheelLock.current = false, 320); } lastWheelAt.current = performance.now(); }
    function onTouchStart(e){ touchStart.vY = e.touches[0].clientY; touchStart.time = performance.now(); }
    function onTouchEnd(e){ if(!openingDone) return; const y = (e.changedTouches && e.changedTouches[0] && e.changedTouches[0].clientY) || touchStart.vY; const delta = touchStart.vY - y; const SW = 40; if(Math.abs(delta) > SW){ const dir = delta > 0 ? 1 : -1; setPage(pageIndexRef.current + dir); } }
    function onKey(e){ if(!openingDone) return; if(["ArrowDown","PageDown"," "].includes(e.key)){ e.preventDefault(); setPage(pageIndexRef.current + 1); } if(["ArrowUp","PageUp"].includes(e.key)){ e.preventDefault(); setPage(pageIndexRef.current - 1); } }
    function onPointer(e){ const now = performance.now(); if(now - lastCursorUpdate.current > 12){ cursor.current.x = e.clientX; cursor.current.y = e.clientY; cursor.current.active = true; lastCursorUpdate.current = now; } }
    function onPointerLeave(){ cursor.current.active = false; }
    function onPointerDown(e){ const px = e.clientX; const py = e.clientY; cursor.current.x = px; cursor.current.y = py; if(bgCanvasRef.current && bgCanvasRef.current._triggerPop) bgCanvasRef.current._triggerPop(px, py); }

    const el = containerRef.current || window;
    el.addEventListener('wheel', onWheel, { passive:false });
    window.addEventListener('touchstart', onTouchStart, { passive:false });
    window.addEventListener('touchend', onTouchEnd);
    window.addEventListener('keydown', onKey);
    window.addEventListener('pointermove', onPointer);
    window.addEventListener('pointerout', onPointerLeave);
    window.addEventListener('pointerdown', onPointerDown);
    return ()=>{ el.removeEventListener('wheel', onWheel); window.removeEventListener('touchstart', onTouchStart); window.removeEventListener('touchend', onTouchEnd); window.removeEventListener('keydown', onKey); window.removeEventListener('pointermove', onPointer); window.removeEventListener('pointerout', onPointerLeave); window.removeEventListener('pointerdown', onPointerDown); };
  }, [height, openingDone, visible.length]);

  /* ---------------- Bricks canvas (teal/indigo/amber) ---------------- */
  useEffect(()=>{
    const canvas = bgCanvasRef.current; if(!canvas) return; const ctx = canvas.getContext('2d'); let W = canvas.width = window.innerWidth; let H = canvas.height = window.innerHeight; const DPR = Math.min(window.devicePixelRatio || 1, 1.6); canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = `${W}px`; canvas.style.height = `${H}px`; ctx.scale(DPR, DPR);

    let BRICK_W = window.innerWidth < 640 ? 120 : 96; let BRICK_H = window.innerWidth < 640 ? 56 : 44; let cols = Math.ceil(W / BRICK_W), rows = Math.ceil(H / BRICK_H); const MAX_BRICKS = 900;
    if(cols * rows > MAX_BRICKS){ const scaleUp = Math.sqrt((cols * rows) / MAX_BRICKS); BRICK_W = Math.ceil(BRICK_W * scaleUp); BRICK_H = Math.ceil(BRICK_H * scaleUp); cols = Math.ceil(W / BRICK_W); rows = Math.ceil(H / BRICK_H); }

    const bricks = [];
    for(let r=0;r<rows;r++){
      for(let c=0;c<cols;c++){
        const x = c * BRICK_W + (r % 2 ? BRICK_W/2 : 0); const y = r * BRICK_H; const cx = x + BRICK_W/2; const cy = y + BRICK_H/2;
        bricks.push({ x, y, w: BRICK_W, h: BRICK_H, baseX: x, baseY: y, cx, cy, pop:0, vy:0, phase: Math.random()*Math.PI*2, falling:false, fallY:0, fallVy:0, opacity:1, gone:false, r, c, mix: Math.random() });
      }
    }

    const pPrimary = hexToRgb(THEME.primary); const pIndigo = hexToRgb(THEME.highlight); const pAmber = hexToRgb(THEME.accent);
    const VIBE_RAMP_MS = 2000;

    canvas._triggerPop = function(px, py){ const R = 160; let nearest=null; let best=Infinity; for(let b of bricks){ if(b.gone) continue; const dx = b.cx - px, dy = b.cy - py; const d = Math.sqrt(dx*dx + dy*dy); if(d < best){ best = d; nearest = b; } if(d < R){ const norm = 1 - d / R; b.pop = Math.max(b.pop, 0.35 * norm); b.vy += (6 * norm) + (Math.random() * 3); } } if(nearest && !nearest.falling && best < Math.max(120, Math.min(W,H) * 0.06)){ nearest.falling = true; nearest.fallVy = Math.random()*4 + 2; nearest.fallY = 0; } };

    let lastT = performance.now();
    function draw(now){ let dt = Math.min(40, now - lastT) * 0.001; if(dt<=0){ bgRAF.current = requestAnimationFrame(draw); return; } lastT = now; ctx.clearRect(0,0,W,H);
      ctx.fillStyle = openingDone ? 'rgba(3,6,10,0.34)' : 'rgba(3,6,10,0.08)'; ctx.fillRect(0,0,W,H);
      const time = now * 0.001; const maxD = Math.max(W,H) * 0.7;
      const activeIdx = activeIndexRef.current || 0; const panelCenterY = (activeIdx * height - currentY.current) + (height/2); const panelCenterX = W/2; const panelW = Math.min(1120, W*0.86); const panelH = Math.min(760, height*0.78);
      const elapsed = Math.max(0, performance.now() - (activeSince.current || performance.now())); const elapsedFactor = Math.min(1, elapsed / VIBE_RAMP_MS);

      for (let b of bricks){ if(b.gone) continue; if(b.falling){ b.fallVy += 900 * dt; b.fallY += b.fallVy * dt; b.opacity = Math.max(0, 1 - (b.fallY / (H * 1.4))); if(b.fallY > H + 100){ b.gone = true; continue; } }
        const dx = b.cx - cursor.current.x; const dy = b.cy - cursor.current.y; const d = Math.sqrt(dx*dx + dy*dy); let t = Math.max(0, 1 - d / maxD); t = Math.pow(t, 0.9); const lift = b.falling ? 0 : Math.min(18, 12 * t + b.pop * 22); const wobble = b.falling ? 0 : Math.sin(b.phase + time * 1.6) * 2 * t;
        if(!b.falling){ b.vy -= 28 * dt; b.vy *= (0.92 - Math.min(0.18, t * 0.12)); b.pop = Math.max(0, b.pop - 0.014); }
        const yOffset = b.falling ? b.fallY : (-lift + b.vy * dt * 60);

        const mixFactor = Math.min(1, Math.max(0, b.mix * 0.9 + 0.1 * Math.sin(time * 0.6 + b.phase)));
        function mix(a,bVal,n){ return Math.round((1-n)*a + n*bVal); }
        const rMid = mix(pPrimary[0], pIndigo[0], mixFactor); const gMid = mix(pPrimary[1], pIndigo[1], mixFactor); const bMid = mix(pPrimary[2], pIndigo[2], mixFactor);
        const rFinal = mix(rMid, pAmber[0], Math.abs(Math.sin(b.phase * 0.2 + time * 0.1)) * 0.18);
        const gFinal = mix(gMid, pAmber[1], Math.abs(Math.cos(b.phase * 0.21 + time * 0.12)) * 0.18);
        const bFinal = mix(bMid, pAmber[2], Math.abs(Math.sin(b.phase * 0.19 + time * 0.14)) * 0.18);

        const dxPanel = Math.abs(b.cx - panelCenterX); const dyPanel = Math.abs(b.cy - panelCenterY);
        let panelDarkness = 0; if(dxPanel < panelW/2 && dyPanel < panelH/2){ const nx = 1 - (dxPanel / (panelW/2)); const ny = 1 - (dyPanel / (panelH/2)); panelDarkness = Math.pow(Math.max(0, Math.min(1, nx * ny)), 1.6) * 0.78; }

        const finalR = Math.max(6, Math.round(rFinal * (1 - panelDarkness))); const finalG = Math.max(6, Math.round(gFinal * (1 - panelDarkness))); const finalB = Math.max(6, Math.round(bFinal * (1 - panelDarkness)));
        const finalAlpha = Math.max(0.56, b.opacity - (panelDarkness * 0.28));

        const distToPanelCenter = Math.hypot(b.cx - panelCenterX, b.cy - panelCenterY); const panelRadius = Math.max(panelW, panelH) * 0.9; const panelInfluence = Math.max(0, 1 - distToPanelCenter / panelRadius);
        const vibAmp = MAX_VIBRATE_AMPLITUDE * panelInfluence * elapsedFactor * 0.9; const vibrate = Math.sin(time * (0.8 + (b.phase % 1))) * vibAmp * 0.7; const rot = Math.sin(time * 0.9 + b.phase) * (vibAmp * 0.02);

        const gx = b.baseX + 6, gy = b.baseY + 6; const grad = ctx.createLinearGradient(gx, gy, gx + b.w - 12, gy + b.h - 12);
        const colorStart = `rgba(${Math.max(0, finalR-22)}, ${Math.max(0, finalG-22)}, ${Math.max(0, finalB-22)}, ${finalAlpha})`;
        const colorEnd = `rgba(${finalR}, ${finalG}, ${finalB}, ${finalAlpha})`;
        grad.addColorStop(0, colorStart); grad.addColorStop(1, colorEnd);

        ctx.save(); ctx.globalAlpha = finalAlpha; ctx.fillStyle = grad;
        if (lift > 2 && !b.falling && bricks.length < 800) { ctx.shadowBlur = Math.min(26, lift * 1.2); ctx.shadowColor = `rgba(0,120,100,${Math.min(0.22, 0.06 * t + 0.06 * b.pop)})`; } else ctx.shadowBlur = 0;
        const px = b.baseX + wobble * 0.6 + vibrate; const py = b.baseY + yOffset;
        ctx.translate(px + (b.w - 12)/2, py + (b.h - 12)/2); ctx.rotate(rot);
        roundRect(ctx, -(b.w - 12)/2, -(b.h - 12)/2, b.w - 12, b.h - 12, 6); ctx.fill();

        if ((t > 0.07 || b.pop > 0.02) && !b.falling && panelDarkness < 0.6) { ctx.shadowBlur = 0; ctx.fillStyle = `rgba(255,255,255,${0.03 * t + 0.04 * b.pop})`; ctx.fillRect(-(b.w - 12)/2 + 6, -(b.h - 12)/2 + 4, b.w - 24, Math.max(3, Math.min(8, 4 * t + 6 * b.pop))); }
        ctx.restore();
      }

      bgRAF.current = requestAnimationFrame(draw);
    }

    function roundRect(ctx,x,y,w,h,r){ const minr = Math.min(r, w/2, h/2); ctx.beginPath(); ctx.moveTo(x + minr, y); ctx.arcTo(x + w, y, x + w, y + h, minr); ctx.arcTo(x + w, y + h, x, y + h, minr); ctx.arcTo(x, y + h, x, y, minr); ctx.arcTo(x, y, x + w, y, minr); ctx.closePath(); }
    function onResize(){ W = canvas.width = window.innerWidth; H = canvas.height = window.innerHeight; canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = `${W}px`; canvas.style.height = `${H}px`; ctx.scale(DPR, DPR); }
    window.addEventListener('resize', onResize);
    bgRAF.current = requestAnimationFrame(draw);
    return ()=>{ window.removeEventListener('resize', onResize); cancelAnimationFrame(bgRAF.current); };
  }, [height, activeIndex, openingDone]);

  // UI transform loop
  useEffect(()=>{
    function frame(){ currentY.current = lerp(currentY.current, targetY.current, (performance.now() - lastWheelAt.current > SNAP_DELAY) ? LERP_SNAP : LERP_FAST); const el = containerRef.current; if(el) el.style.transform = `translate3d(0, ${-currentY.current}px, 0)`; chapterRefs.current.forEach((node, i)=>{ if(!node) return; const center = i * height; const offset = (currentY.current - center)/height; const abs = Math.abs(offset); const translateY = offset * 28; const rotate = offset * 3.2; const scale = 1 - Math.min(0.08, abs * 0.08); const opacity = 1 - Math.min(0.9, abs * 0.9); node.style.transform = `translate3d(0px, ${translateY}px, 0) rotate(${rotate}deg) scale(${scale})`; node.style.opacity = String(opacity); }); const idx = Math.round(currentY.current / height); if(activeIndexRef.current !== idx){ activeIndexRef.current = idx; setActiveIndex(idx); activeSince.current = performance.now(); } rafRef.current = requestAnimationFrame(frame); }
    rafRef.current = requestAnimationFrame(frame); return ()=> cancelAnimationFrame(rafRef.current);
  }, [height, visible.length]);

  function scrollToIndex(i){ const max = Math.max(0, (visible.length - 1) * height); const dest = Math.max(0, Math.min(i * height, max)); targetY.current = dest; lastWheelAt.current = performance.now(); pageIndexRef.current = Math.round(dest / height); setPageIndex(pageIndexRef.current); activeSince.current = performance.now(); }

  const AUTO_ADVANCE_DELAY_MS = 220;
  function handleAnswer(qid, value){ setAnswers(prev => ({ ...prev, [qid]: value })); const questionDef = QUESTIONS.find(x => x.id === qid) || {}; let gained = 0; if(questionDef.type === 'mcq' && questionDef.points) gained = questionDef.points[value] || 0; else if(questionDef.type === 'rating') gained = value * (questionDef.pointsPerStar || 1); else if(questionDef.type === 'text') gained = questionDef.points || 0; if(gained){ setScore(s => s + gained); pulseScore(); } if(questionDef.type === 'mcq' || questionDef.type === 'rating'){ const next = Math.min(activeIndexRef.current + 1, visible.length - 1); setTimeout(()=> scrollToIndex(next), AUTO_ADVANCE_DELAY_MS); } }

  function pulseScore(){ const el = document.getElementById('score-badge'); if(!el) return; el.animate([{ transform:'translateY(0) scale(1)' }, { transform:'translateY(-6px) scale(1.06)' }, { transform:'translateY(0) scale(1)' }], { duration:520, easing:'ease-out' }); }
  function onMiniGameComplete(success){ if(success){ setScore(s => s + 30); pulseScore(); } setAnswers(prev => ({ ...prev, q4: success ? 'success' : 'fail' })); setTimeout(()=> scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)), 300); }
  function finish(){ setShowFinish(true); if(canvasRef.current) confettiBurst(canvasRef.current); }
  function resetAll(){ if(!window.confirm('Reset survey? This clears local progress.')) return; setAnswers({}); setScore(0); setShowFinish(false); targetY.current = 0; currentY.current = 0; pageIndexRef.current = 0; setPageIndex(0); if(containerRef.current) containerRef.current.style.transform = 'translate3d(0,0,0)'; }

  /* --------------- ChapterContent (with grounded left card) --------------- */
  function ChapterContent({ item, idx }){
    if(item.type === 'stage'){
      const isFirst = idx === 0; const isLast = idx === Math.max(0, visible.length - 1);
      return (
        <div style={{ display:'flex', gap:20, alignItems:'center', justifyContent:'center', flexDirection:'column' }}>
          <div style={{ textAlign:'center' }}>
            <motion.h1 initial={{ y:40, opacity:0 }} animate={{ y:0, opacity:1 }} transition={{ duration:0.8 }} style={{ fontSize:'2.4rem', fontWeight:900, background:`linear-gradient(90deg, ${THEME.primary}, ${THEME.highlight})`, WebkitBackgroundClip:'text', color:'transparent', textShadow:'0 8px 28px rgba(0,245,212,0.06)' }}>{item.title}</motion.h1>
            {item.subtitle && <motion.p initial={{ y:20, opacity:0 }} animate={{ y:0, opacity:0.95 }} transition={{ delay:0.12 }} style={{ color: THEME.muted, marginTop:8 }}>{item.subtitle}</motion.p>}
          </div>

          <div style={{ display:'flex', gap:16, alignItems:'center', justifyContent:'center' }}>
            {isLast ? (
              <>
                <button onClick={() => resetAll()} className="btn btn-primary">Restart</button>
                <button onClick={() => setShowFinish(true)} className="btn btn-ghost">Finish</button>
              </>
            ) : (
              <>
                <button onClick={() => scrollToIndex(Math.min(idx + 1, visible.length - 1))} className="btn btn-primary">Enter</button>
                {!isFirst && <button onClick={() => finish()} className="btn btn-ghost">Skip</button>}
              </>
            )}
          </div>
        </div>
      );
    }

    if(item.type === 'mcq'){
      // creative MCQ cards with subtle icon and animated border
      return (
        <div style={{ width:'100%', maxWidth:980 }}>
          <div style={{ color: THEME.muted, marginBottom:12 }}>{item.flavor}</div>
          <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit, minmax(240px, 1fr))', gap:14 }}>
            {item.choices.map(c => {
              const selected = answers[item.id] === c;
              return (
                <motion.button key={c} whileHover={{ scale:1.02 }} onClick={() => handleAnswer(item.id, c)} style={{ padding:18, borderRadius:18, position:'relative', overflow:'hidden', textAlign:'left', display:'flex', flexDirection:'column', gap:8, justifyContent:'space-between', minHeight:110, boxShadow: selected ? '0 18px 48px rgba(0,245,212,0.06)' : 'none', border: selected ? '1px solid rgba(255,255,255,0.04)' : '1px solid rgba(255,255,255,0.02)', background: selected ? `linear-gradient(180deg, rgba(0,245,212,0.12), rgba(110,123,255,0.06))` : 'rgba(255,255,255,0.02)', color: selected ? '#02171A' : THEME.text }}>
                  <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center' }}>
                    <div style={{ fontWeight:900, fontSize:16 }}>{c}</div>
                    <div style={{ fontSize:12, color: THEME.muted }}>{item.points && item.points[c] ? `${item.points[c]} pts` : ''}</div>
                  </div>
                  <div style={{ display:'flex', gap:10, alignItems:'center' }}>
                    <div style={{ width:44, height:44, borderRadius:10, background: 'linear-gradient(135deg, rgba(255,255,255,0.03), rgba(255,255,255,0.02))', display:'flex', alignItems:'center', justifyContent:'center' }}>
                      <svg width="20" height="20" viewBox="0 0 24 24" fill="none"><path d="M12 2l3 7h7l-5.5 4 2 7L12 17l-6.5 3 2-7L2 9h7l3-7z" stroke={selected ? THEME.primary : 'rgba(255,255,255,0.18)'} strokeWidth="0.9"/></svg>
                    </div>
                    <div style={{ color: THEME.muted, fontSize:13 }}>Tap to select — you can change your mind</div>
                  </div>
                  {selected && <motion.div initial={{ opacity:0, y:6 }} animate={{ opacity:1, y:0 }} transition={{ duration:0.28 }} style={{ position:'absolute', right:10, top:10, padding:'6px 8px', borderRadius:10, background:'rgba(0,0,0,0.55)', color:THEME.text, fontSize:12, fontWeight:700 }}>Selected</motion.div>}
                </motion.button>
              );
            })}
          </div>
        </div>
      );
    }

    if(item.type === 'rating'){
      return (
        <div style={{ textAlign:'center' }}>
          <div style={{ color: THEME.muted, marginBottom:12 }}>{item.flavor}</div>
          <div style={{ display:'flex', gap:14, justifyContent:'center' }}>
            {Array.from({ length: item.scale }).map((_, i) => {
              const sel = answers[item.id] === i+1;
              return (
                <motion.button key={i} onClick={() => handleAnswer(item.id, i+1)} whileTap={{ scale:0.96 }} style={{ width:64, height:64, borderRadius:999, display:'flex', alignItems:'center', justifyContent:'center', border: sel ? 'none' : '1px solid rgba(255,255,255,0.03)', background: sel ? `linear-gradient(90deg, ${THEME.primary}, ${THEME.highlight})` : 'rgba(255,255,255,0.02)', color: sel ? '#02171A' : THEME.text, fontWeight:800, fontSize:18 }}>
                  {i+1}
                </motion.button>
              );
            })}
          </div>
        </div>
      );
    }

    if(item.type === 'text'){
      return (
        <div style={{ maxWidth:980, margin:'0 auto' }}>
          <div style={{ color: THEME.muted, marginBottom:12 }}>{item.flavor}</div>
          <textarea maxLength={item.maxLength} rows={5} value={answers[item.id] || ''} onChange={(e) => setAnswers(prev => ({ ...prev, [item.id]: e.target.value }))} placeholder="One-line pitch..." style={{ width:'100%', padding:16, borderRadius:12, background: THEME.panel, border:'1px solid rgba(255,255,255,0.03)', color: THEME.text, resize:'vertical' }} />
          <div style={{ display:'flex', justifyContent:'space-between', marginTop:10 }}>
            <div style={{ color: THEME.muted }}>{(answers[item.id] || '').length}/{item.maxLength}</div>
            <div style={{ display:'flex', gap:10 }}>
              <button onClick={() => { if(item.points && (answers[item.id] || '').length > 0){ setScore(s => s + item.points); pulseScore(); } scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)); }} className="btn btn-primary">Submit</button>
              <button onClick={() => setAnswers(prev => ({ ...prev, [item.id]: '' }))} className="btn btn-ghost">Clear</button>
            </div>
          </div>
        </div>
      );
    }

    if(item.type === 'minigame') return <MiniGame timeLimit={item.timeLimit} onComplete={onMiniGameComplete} flavor={item.flavor} />;
    return null;
  }

  // small helpers
  function pulseScore(){ const el = document.getElementById('score-badge'); if(!el) return; el.animate([{ transform:'translateY(0) scale(1)' }, { transform:'translateY(-6px) scale(1.06)' }, { transform:'translateY(0) scale(1)' }], { duration:520, easing:'ease-out' }); }
  function onMiniGameComplete(success){ if(success){ setScore(s => s + 30); pulseScore(); } setAnswers(prev => ({ ...prev, q4: success ? 'success' : 'fail' })); setTimeout(()=> scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)), 300); }
  function finish(){ setShowFinish(true); if(canvasRef.current) confettiBurst(canvasRef.current); }
  function resetAll(){ if(!window.confirm('Reset survey? This clears local progress.')) return; setAnswers({}); setScore(0); setShowFinish(false); targetY.current = 0; currentY.current = 0; pageIndexRef.current = 0; setPageIndex(0); if(containerRef.current) containerRef.current.style.transform = 'translate3d(0,0,0)'; }

  // base layout & render
  return (
    <div style={{ minHeight:'100vh', position:'relative', overflow:'hidden', background: THEME.bg, color: THEME.text }}>
      <style>{`
        :root{ --primary: ${THEME.primary}; --accent: ${THEME.accent}; --highlight: ${THEME.highlight}; --muted: ${THEME.muted}; --text: ${THEME.text}; }
        .btn { padding: 10px 14px; border-radius: 12px; font-weight:800; cursor:pointer; border: 1px solid rgba(255,255,255,0.03); }
        .btn-primary { background: linear-gradient(90deg, var(--primary), var(--highlight)); color: #02171A; box-shadow: 0 10px 36px rgba(0,245,212,0.06); }
        .btn-ghost { background: rgba(255,255,255,0.02); color: var(--text); border: 1px solid rgba(255,255,255,0.03); }
        /* chapter left card */
        .chapter-card { background: linear-gradient(180deg, rgba(255,255,255,0.02), rgba(255,255,255,0.01)); border-radius:16px; padding:18px; border: 1px solid rgba(255,255,255,0.03); box-shadow: 0 10px 30px rgba(3,6,10,0.6); }
      `}</style>

      <CustomCursor />

      <canvas ref={bgCanvasRef} className="fixed inset-0 z-0 pointer-events-none" />
      <canvas ref={canvasRef} className="fixed inset-0 z-50 pointer-events-none" />

      <div style={{ position:'fixed', right:18, top:18, zIndex:100, display:'flex', flexDirection:'column', alignItems:'flex-end', gap:8 }}>
        <div id="score-badge" style={{ padding:'10px 16px', borderRadius:999, background:`linear-gradient(90deg, ${THEME.primary}, ${THEME.accent})`, color:'#02171A', fontWeight:900, boxShadow:'0 8px 30px rgba(0,245,212,0.06)' }}>{score} pts</div>
        <div style={{ color: THEME.muted, fontSize:12 }}>Level {Math.floor(score/50)+1}</div>
      </div>

      <nav style={{ position:'fixed', left:18, top:'50%', transform:'translateY(-50%)', zIndex:100, display:'flex', flexDirection:'column', gap:10 }}>
        {visible.map((_, i) => (
          <button key={i} onClick={() => scrollToIndex(i)} aria-label={`Go to chapter ${i+1}`} style={{ width:12, height:12, borderRadius:999, background: i === activeIndex ? `linear-gradient(90deg, ${THEME.primary}, ${THEME.highlight})` : 'rgba(255,255,255,0.03)', border:'1px solid rgba(255,255,255,0.02)' }} />
        ))}
      </nav>

      <div ref={containerRef} className="fixed inset-0 top-0 left-0 w-full will-change-transform z-40">
        <div style={{ height: `${visible.length * height}px` }}>
          {visible.map((item, i) => (
            <section key={item.id} ref={addChapterRef} style={{ height: `${height}px` }} className="flex items-center justify-center px-6 md:px-12">
              <div style={{ width:'100%', maxWidth:1120, margin:'0 auto', display:'grid', gridTemplateColumns:'320px 1fr', gap:20, alignItems:'center' }}>

                {/* Left column: grounded card with chapter info */}
                <aside style={{ display: window.innerWidth >= 1024 ? 'block' : 'none' }}>
                  <div className="chapter-card" style={{ width:280 }}>
                    <div style={{ color: THEME.muted, fontSize:13 }}>Chapter</div>
                    <div style={{ display:'flex', alignItems:'center', gap:12, marginTop:10 }}>
                      <div style={{ width:64, height:64, borderRadius:12, background:`linear-gradient(135deg, ${THEME.primary}, ${THEME.highlight})`, display:'flex', alignItems:'center', justifyContent:'center', boxShadow:'0 12px 40px rgba(0,245,212,0.06)' }}>
                        <svg width="30" height="30" viewBox="0 0 24 24" fill="none"><path d="M12 2l3 7h7l-5.5 4 2 7L12 17l-6.5 3 2-7L2 9h7l3-7z" stroke="#02171A" strokeWidth="0.9"/></svg>
                      </div>
                      <div>
                        <div style={{ fontSize:20, fontWeight:900, color: THEME.text }}>{String(i+1).padStart(2,'0')}</div>
                        <div style={{ color: THEME.muted, fontSize:13, marginTop:4 }}>{item.title}</div>
                      </div>
                    </div>

                    <div style={{ marginTop:12, color: THEME.muted, fontSize:13 }}>Quick tips</div>
                    <ul style={{ marginTop:8, color: THEME.muted, paddingLeft:18 }}>
                      <li style={{ marginBottom:6 }}>Tap choices quickly to earn momentum.</li>
                      <li style={{ marginBottom:6 }}>Use arrow keys or scroll to jump pages.</li>
                    </ul>

                    <div style={{ marginTop:12, display:'flex', gap:8 }}>
                      <button onClick={() => scrollToIndex(Math.max(0, i-1))} className="btn btn-ghost" style={{ flex:1 }}>Prev</button>
                      <button onClick={() => scrollToIndex(Math.min(i+1, visible.length-1))} className="btn btn-primary" style={{ flex:1 }}>Next</button>
                    </div>
                  </div>
                </aside>

                {/* Right (main) column: card content */}
                <div style={{ borderRadius:14, padding:28, textAlign:'center', background:'rgba(0,0,0,0.46)', border:'1px solid rgba(255,255,255,0.03)', backdropFilter:'blur(8px)', minHeight:210 }}>
                  <motion.div initial={{ opacity:0, y:30 }} animate={{ opacity:1, y:0 }} transition={{ duration:0.6 }}>
                    <ChapterContent item={item} idx={i} />
                  </motion.div>
                </div>

              </div>
            </section>
          ))}
        </div>
      </div>

      <AnimatePresence>{showFinish && (
        <motion.div initial={{ opacity:0 }} animate={{ opacity:1 }} exit={{ opacity:0 }} style={{ position:'fixed', inset:0, zIndex:90, display:'flex', alignItems:'center', justifyContent:'center' }}>
          <div style={{ width:'min(720px,92%)', padding:28, borderRadius:18, background:'rgba(0,0,0,0.6)', border:'1px solid rgba(255,255,255,0.04)', backdropFilter:'blur(8px)' }}>
            <h2 style={{ color: THEME.text, fontSize:28, fontWeight:900 }}>All done 🎉</h2>
            <p style={{ color: THEME.muted, marginTop:10 }}>You scored <strong style={{ color: THEME.text }}>{score}</strong>. Thanks for playing!</p>
            <div style={{ marginTop:18, display:'flex', justifyContent:'center' }}>
              <button onClick={() => resetAll()} className="btn btn-primary">Reset</button>
            </div>
          </div>
        </motion.div>
      )}</AnimatePresence>

    </div>
  );
}
