import React, { useEffect, useState, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";

/**
 * Full revised app.js
 * - Auto video -> remote fallback -> animated-gradient fallback
 * - Bricks use per-brick purple-cyan gradient variation
 * - Professional loading bar + visuals
 *
 * Put your local video at public/opening.mp4 or change OPENING_VIDEO_SRC.
 */

const OPENING_VIDEO_SRC = "/opening.mp4"; // local video (public/)
const REMOTE_FALLBACK = "https://interactive-examples.mdn.mozilla.net/media/cc0-videos/flower.mp4"; // only used as test fallback

const THEME = {
  purple: "#7c3aed",
  cyan: "#06b6d4",
  magenta: "#ff4791",
  bg: "#04050a",
  panel: "rgba(255,255,255,0.02)",
  text: "#e6eef8",
  muted: "#9aa6b2",
};

const QUESTIONS = [
  { id: "intro", type: "stage", title: "Nightfall at HackPlay", subtitle: "Step into a neon-lit hall. Your choices tune the journey." },
  {
    id: "q1",
    type: "mcq",
    title: "Entrance — choose your mindset",
    choices: ["Build something cool", "Learn fast", "Meet people", "Chase the prizes"],
    points: { "Build something cool": 20, "Learn fast": 10, "Meet people": 15, "Chase the prizes": 5 },
    flavor: "The doors close. A low bass. Pick the vibe that follows you.",
  },
  {
    id: "q2",
    type: "rating",
    title: "Charge Meter — how hyped are you?",
    scale: 5,
    pointsPerStar: 6,
    flavor: "The stage lights react to your energy.",
  },
  {
    id: "q3",
    type: "text",
    title: "One-line pitch — what's the idea?",
    maxLength: 140,
    points: 15,
    flavor: "A single line — the fragment your team will build around.",
  },
  {
    id: "q4",
    type: "minigame",
    title: "Charge the core — tap to power your idea",
    timeLimit: 6000,
    pointsOnSuccess: 30,
    flavor: "Fast reflexes prove prototype-ready energy.",
  },
  {
    id: "q5",
    type: "mcq",
    title: "Team vibe — pick your tribe",
    choices: ["Chill coders", "All-nighters", "Balanced squad", "Solo hero"],
    points: { "Chill coders": 10, "All-nighters": 20, "Balanced squad": 15, "Solo hero": 5 },
    flavor: "Who will you ride the night with?",
    dependsOn: { q1: "Build something cool" },
  },
  { id: "end", type: "stage", title: "Curtain Call", subtitle: "Collect badges, revisit your pitch, and step back into the light." },
];

function lerp(a, b, n) { return (1 - n) * a + n * b; }
function hexToRgb(hex) {
  const h = hex.replace("#", "");
  return [parseInt(h.substring(0,2),16), parseInt(h.substring(2,4),16), parseInt(h.substring(4,6),16)];
}

/* ---------------------------
   VideoBackground: plays video and reports ready/error
   --------------------------- */
function VideoBackground({ src, videoRef, onReady, onError, keyOverride }) {
  const internalRef = useRef(null);
  const ref = videoRef || internalRef;

  useEffect(() => {
    const v = ref.current;
    if (!v) return;
    v.muted = true;
    v.playsInline = true;
    v.preload = "auto";
    // attempt play (muted => autoplay allowed)
    const p = v.play && v.play();
    if (p && p.catch) p.catch(() => { /* ignore */ });

    let fired = false;
    function markReady() { if (fired) return; fired = true; onReady && onReady(); }
    function markError(e) { if (fired) return; fired = true; onError && onError(e); }

    function onCanPlay() { markReady(); }
    function onErr(e) { console.warn("video error:", e); markError(e); }

    v.addEventListener("canplaythrough", onCanPlay);
    v.addEventListener("canplay", onCanPlay);
    v.addEventListener("loadeddata", onCanPlay);
    v.addEventListener("loadedmetadata", onCanPlay);
    v.addEventListener("error", onErr);

    // tap to force-ready on mobile
    v.addEventListener("click", onCanPlay);

    const safety = setTimeout(() => { if (!fired) markError(new Error("video-safety-timeout")); }, 4500);

    return () => {
      v.removeEventListener("canplaythrough", onCanPlay);
      v.removeEventListener("canplay", onCanPlay);
      v.removeEventListener("loadeddata", onCanPlay);
      v.removeEventListener("loadedmetadata", onCanPlay);
      v.removeEventListener("error", onErr);
      v.removeEventListener("click", onCanPlay);
      clearTimeout(safety);
    };
  }, [src, onReady, onError, keyOverride, ref]);

  return (
    <video
      key={keyOverride}
      ref={ref}
      src={src}
      muted
      loop
      playsInline
      autoPlay
      preload="auto"
      style={{ position: "fixed", inset: 0, width: "100%", height: "100%", objectFit: "cover", zIndex: -50, pointerEvents: "auto", opacity: 1 }}
    />
  );
}

/* ---------------------------
   AnimatedGradientFallback: professional-looking animated background
   --------------------------- */
function AnimatedGradientFallback() {
  // CSS-only animated gradient + subtle noise overlay
  return (
    <>
      <div className="fixed inset-0 -z-55 animated-gradient" />
      <svg className="fixed inset-0 w-full h-full -z-50 pointer-events-none" style={{ opacity: 0.06 }} xmlns="http://www.w3.org/2000/svg">
        <filter id="noiseFilter"><feTurbulence baseFrequency="0.9" numOctaves="2" stitchTiles="stitch" /></filter>
        <rect width="100%" height="100%" filter="url(#noiseFilter)" />
      </svg>

      <style>{`
        .animated-gradient {
          background: linear-gradient(120deg, ${THEME.purple}, ${THEME.cyan}, ${THEME.magenta});
          background-size: 200% 200%;
          animation: gradientShift 12s ease infinite;
          opacity: 1;
        }
        @keyframes gradientShift {
          0% { background-position: 0% 50% }
          50% { background-position: 100% 50% }
          100% { background-position: 0% 50% }
        }
      `}</style>
    </>
  );
}

/* ---------------------------
   LoadingOverlay: improved professional bar + shimmer
   --------------------------- */
function LoadingOverlay({ progress }) {
  return (
    <div className="fixed inset-0 z-80 flex items-center justify-center pointer-events-none">
      <div style={{ width: "min(880px, 92%)", padding: 18, textAlign: "center" }}>
        <div style={{ color: THEME.text, fontWeight: 600, marginBottom: 10, fontSize: 16 }}>Preparing the experience</div>

        <div style={{ position: "relative", height: 18, width: "100%", borderRadius: 12, overflow: "hidden", background: "rgba(255,255,255,0.03)", boxShadow: "inset 0 1px 0 rgba(255,255,255,0.02)" }}>
          <div className="progress-fill" style={{ width: `${progress}%`, height: "100%", borderRadius: 12, transition: "width 360ms cubic-bezier(.2,.9,.2,1)" }} />
          <div className="progress-shimmer" />
        </div>

        <div style={{ marginTop: 10, display: "flex", justifyContent: "space-between", color: THEME.muted, fontSize: 13 }}>
          <div>{Math.round(progress)}%</div>
          <div>Loading assets…</div>
        </div>
      </div>

      <style>{`
        .progress-fill {
          background: linear-gradient(90deg, ${THEME.purple}, ${THEME.cyan});
          box-shadow: 0 8px 30px rgba(124,58,237,0.12), inset 0 -4px 14px rgba(0,0,0,0.2);
        }
        .progress-shimmer {
          position: absolute; left: 0; top: 0; bottom: 0; width: 40%; transform: translateX(-100%); background: linear-gradient(90deg, rgba(255,255,255,0.06), rgba(255,255,255,0.0), rgba(255,255,255,0.06)); mix-blend-mode: overlay; animation: shimmerMove 1.6s linear infinite;
        }
        @keyframes shimmerMove {
          0% { transform: translateX(-100%); opacity: 0.6 }
          50%{ transform: translateX(30%); opacity: 1 }
          100%{ transform: translateX(120%); opacity: 0.6 }
        }
      `}</style>
    </div>
  );
}

/* ---------------------------
   CustomCursor (kept, themed)
   --------------------------- */
function CustomCursor() {
  const dotRef = useRef(null);
  const ringRef = useRef(null);
  const pos = useRef({ x: typeof window !== "undefined" ? window.innerWidth/2 : 0, y: typeof window !== "undefined" ? window.innerHeight/2 : 0 });
  const hover = useRef(false);
  const rafRef = useRef(null);

  useEffect(() => {
    const dot = dotRef.current; const ring = ringRef.current;
    if(!dot || !ring) return;

    function onMouseMove(e){ pos.current.x = e.clientX; pos.current.y = e.clientY; }
    function onHoverChange(e){ const t = e.target; hover.current = !!(t.closest && t.closest('button, a, [role="button"], input, textarea')); }

    window.addEventListener('mousemove', onMouseMove);
    window.addEventListener('mouseover', onHoverChange);

    let cx = pos.current.x, cy = pos.current.y, ringScale = 1;
    function loop(){
      cx = lerp(cx, pos.current.x, 0.18);
      cy = lerp(cy, pos.current.y, 0.18);
      dot.style.transform = `translate3d(${cx-6}px, ${cy-6}px, 0)`;
      const rx = lerp(parseFloat(ring.dataset.x || cx), cx, 0.08);
      const ry = lerp(parseFloat(ring.dataset.y || cy), cy, 0.08);
      ring.dataset.x = rx; ring.dataset.y = ry;
      ringScale = lerp(ringScale, hover.current ? 1.9 : 1, 0.12);
      ring.style.transform = `translate3d(${rx-18}px, ${ry-18}px, 0) scale(${ringScale})`;
      rafRef.current = requestAnimationFrame(loop);
    }
    rafRef.current = requestAnimationFrame(loop);

    return () => { window.removeEventListener('mousemove', onMouseMove); window.removeEventListener('mouseover', onHoverChange); cancelAnimationFrame(rafRef.current); };
  }, []);

  useEffect(() => { document.documentElement.classList.add('custom-cursor-hide'); return () => document.documentElement.classList.remove('custom-cursor-hide'); }, []);

  return (
    <>
      <div ref={ringRef} style={{ position: 'fixed', left: 0, top: 0, width:36, height:36, borderRadius:9999, border:`1.5px solid rgba(255,255,255,0.06)`, pointerEvents:'none', zIndex:100000, boxShadow:`0 8px 30px rgba(124,58,237,0.08)` }} />
      <div ref={dotRef} style={{ position: 'fixed', left:0, top:0, width:12, height:12, borderRadius:9999, background: 'radial-gradient(circle at 30% 30%, #fff, #dcdcdc)', pointerEvents:'none', zIndex:100001, boxShadow:'0 8px 26px rgba(124,58,237,0.12)' }} />
      <style>{`.custom-cursor-hide, .custom-cursor-hide * { cursor: none !important; }`}</style>
    </>
  );
}

/* ---------------------------
   MiniGame (kept)
   --------------------------- */
function MiniGame({ timeLimit = 6000, onComplete, flavor }) {
  const [charge, setCharge] = useState(0);
  const runningRef = useRef(false);
  const raf = useRef(null);
  const startedAt = useRef(0);
  const manualBoost = useRef(0);
  useEffect(() => () => cancelAnimationFrame(raf.current), []);

  function start() { if (runningRef.current) return; runningRef.current = true; manualBoost.current = 0; startedAt.current = performance.now(); tick(); }
  function tick() {
    raf.current = requestAnimationFrame(() => {
      const elapsed = performance.now() - startedAt.current;
      const base = Math.min(100, Math.round((elapsed / timeLimit) * 100));
      const total = Math.min(100, base + manualBoost.current);
      setCharge(total);
      if (total >= 100) { runningRef.current = false; cancelAnimationFrame(raf.current); onComplete(true); return; }
      if (elapsed >= timeLimit) { runningRef.current = false; cancelAnimationFrame(raf.current); onComplete(total >= 60); return; }
      tick();
    });
  }
  function handleTap() { if (!runningRef.current) start(); else { manualBoost.current = Math.min(100, manualBoost.current + 12); setCharge(c => Math.min(100, c + 12)); } }

  return (
    <div className="w-full flex flex-col items-center gap-4">
      <div style={{ color: THEME.muted, fontSize: 13 }}>{flavor}</div>
      <div className="relative w-56 h-56 flex items-center justify-center">
        <motion.div onClick={handleTap} whileTap={{ scale: 0.9 }} whileHover={{ scale: 1.04 }} transition={{ type: 'spring', stiffness: 300 }} className="w-40 h-40 rounded-full shadow-2xl flex items-center justify-center cursor-pointer relative overflow-hidden" style={{ background: `linear-gradient(135deg, ${THEME.purple}, ${THEME.magenta})` }}>
          <motion.div animate={{ rotate: [0,360] }} transition={{ repeat: Infinity, duration: 8, ease: 'linear' }} className="absolute inset-0 opacity-18" />
          <div style={{ color: '#fff', fontWeight: 800, fontSize: 18 }}>TAP</div>
        </motion.div>
      </div>
      <div style={{ width: '100%', maxWidth: 540 }}>
        <div style={{ color: THEME.muted, marginBottom: 6 }}>Charge: {charge}%</div>
        <div style={{ width: '100%', height: 12, borderRadius: 10, overflow: 'hidden', background: 'rgba(255,255,255,0.02)' }}>
          <motion.div initial={{ width: '0%' }} animate={{ width: `${charge}%` }} transition={{ type: 'spring', stiffness: 140, damping: 16 }} style={{ height: '100%', borderRadius: 10, background: `linear-gradient(90deg, ${THEME.purple}, ${THEME.cyan})` }} />
        </div>
      </div>
    </div>
  );
}

/* ---------------------------
   Main component
   --------------------------- */
export default function GamifiedSurveyForm() {
  // persisted answers / score
  const [answers, setAnswers] = useState(() => { try { const raw = localStorage.getItem('gamifiedSurveyAnswers'); return raw ? JSON.parse(raw) : {}; } catch { return {}; } });
  const [score, setScore] = useState(() => Number(localStorage.getItem('gamifiedSurveyScore')) || 0);

  // UI states
  const [openingDone, setOpeningDone] = useState(false);
  const [loadProgress, setLoadProgress] = useState(0);
  const [videoPhase, setVideoPhase] = useState('local'); // 'local'|'remote'|'failed'|'ready'
  const [videoKey, setVideoKey] = useState(0); // bump to remount video element
  const [showVideoErrorNotice, setShowVideoErrorNotice] = useState(false);
  const [showResponses, setShowResponses] = useState(false);
  const [showFinish, setShowFinish] = useState(false);

  const canvasRef = useRef(null);   // confetti
  const bgCanvasRef = useRef(null); // bricks
  const videoRef = useRef(null);

  useEffect(() => localStorage.setItem('gamifiedSurveyAnswers', JSON.stringify(answers)), [answers]);
  useEffect(() => localStorage.setItem('gamifiedSurveyScore', String(score)), [score]);

  const visible = QUESTIONS.filter(q => { if (!q.dependsOn) return true; const keys = Object.keys(q.dependsOn); return keys.every(k => answers[k] === q.dependsOn[k]); });

  // layout & scroll states
  const [height, setHeight] = useState(typeof window !== 'undefined' ? window.innerHeight : 800);
  useEffect(() => { function onResize(){ setHeight(window.innerHeight); } window.addEventListener('resize', onResize); return () => window.removeEventListener('resize', onResize); }, []);

  useEffect(() => {
    const prevOverflow = document.documentElement.style.overflow;
    const prevTouch = document.body.style.touchAction;
    document.documentElement.style.overflow = 'hidden';
    document.body.style.touchAction = 'none';
    return () => { document.documentElement.style.overflow = prevOverflow; document.body.style.touchAction = prevTouch; };
  }, []);

  const containerRef = useRef(null);
  const chapterRefs = useRef([]);
  chapterRefs.current = [];
  const addChapterRef = (el) => { if (el) chapterRefs.current.push(el); };

  const targetY = useRef(0);
  const currentY = useRef(0);
  const rafRef = useRef(null);
  const lastWheelAt = useRef(0);
  const isScrolling = useRef(false);

  const SNAP_DELAY = 70;
  const LERP_FAST = 0.42;
  const LERP_SNAP = 0.62;

  const cursor = useRef({ x: typeof window !== 'undefined' ? window.innerWidth/2 : 0, y: typeof window !== 'undefined' ? window.innerHeight/2 : 0, active: false });
  const lastCursorUpdate = useRef(0);
  const bgRAF = useRef(null);

  const activeIndexRef = useRef(0);
  const [activeIndex, setActiveIndex] = useState(0);

  // loading simulation tied to video readiness
  useEffect(() => {
    let raf = null;
    let simulated = 0;
    const start = performance.now();
    function tick() {
      // approach ~92% while waiting, jump when ready
      simulated = Math.min(92, simulated + (100 - simulated) * 0.05 + 0.6);
      setLoadProgress(prev => Math.max(prev, simulated));
      raf = requestAnimationFrame(tick);
    }
    raf = requestAnimationFrame(tick);
    return () => cancelAnimationFrame(raf);
  }, []);

  // Video callbacks
  function onVideoReady() {
    // smooth finish
    setLoadProgress(100);
    setTimeout(() => { setOpeningDone(true); setVideoPhase('ready'); setShowVideoErrorNotice(false); }, 260);
  }

  // If a video errors: auto-switch from local -> remote once, then to fallback
  function onVideoError(e) {
    console.warn("Video error:", e);
    if (videoPhase === 'local') {
      // try remote fallback automatically
      setVideoPhase('remote');
      setVideoKey(k => k + 1);
      // small UI hint
      setShowVideoErrorNotice(true);
      console.warn("Local video failed — trying remote fallback");
      return;
    }
    if (videoPhase === 'remote') {
      // both failed -> use animated gradient fallback
      setVideoPhase('failed');
      setShowVideoErrorNotice(true);
      // finish loading and reveal UI
      setLoadProgress(100);
      setTimeout(() => { setOpeningDone(true); }, 420);
      console.warn("Both local and remote fallback failed. Using animated-gradient fallback.");
      return;
    }
    // if already failed do nothing
  }

  // user-triggered retry (re-try local first)
  function retryVideos() {
    setVideoPhase('local');
    setVideoKey(k => k + 1);
    setShowVideoErrorNotice(false);
    setOpeningDone(false);
    setLoadProgress(0);
  }

  // input handlers
  useEffect(() => {
    const isTouching = { v: false };
    const touchStartY = { v: 0 };
    function clampTarget(){ const max = Math.max(0, (visible.length - 1) * height); if (targetY.current < 0) targetY.current = 0; if (targetY.current > max) targetY.current = max; }
    function onWheel(e){ e.preventDefault(); targetY.current += e.deltaY * 1.2; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; }
    function onTouchStart(e){ touchStartY.v = e.touches[0].clientY; isTouching.v = true; }
    function onTouchMove(e){ if(!isTouching.v) return; const y = e.touches[0].clientY; const delta = touchStartY.v - y; targetY.current += delta; touchStartY.v = y; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; }
    function onTouchEnd(){ isTouching.v = false; }
    function onKey(e){ if(e.key === 'ArrowDown'){ targetY.current += height * 0.95; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; } if(e.key === 'ArrowUp'){ targetY.current -= height * 0.95; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; } }

    const el = containerRef.current || window;
    el.addEventListener('wheel', onWheel, { passive: false });
    window.addEventListener('touchstart', onTouchStart, { passive: false });
    window.addEventListener('touchmove', onTouchMove, { passive: false });
    window.addEventListener('touchend', onTouchEnd);
    window.addEventListener('keydown', onKey);

    function onPointer(e){ const now = performance.now(); if (now - lastCursorUpdate.current > 12) { cursor.current.x = e.clientX; cursor.current.y = e.clientY; cursor.current.active = true; lastCursorUpdate.current = now; } }
    function onPointerLeave(){ cursor.current.active = false; }
    function onPointerDown(e){ const px = e.clientX; const py = e.clientY; cursor.current.x = px; cursor.current.y = py; if (bgCanvasRef.current && bgCanvasRef.current._triggerPop) bgCanvasRef.current._triggerPop(px, py); }

    window.addEventListener('pointermove', onPointer);
    window.addEventListener('pointerout', onPointerLeave);
    window.addEventListener('pointerdown', onPointerDown);

    return () => {
      el.removeEventListener('wheel', onWheel);
      window.removeEventListener('touchstart', onTouchStart);
      window.removeEventListener('touchmove', onTouchMove);
      window.removeEventListener('touchend', onTouchEnd);
      window.removeEventListener('keydown', onKey);
      window.removeEventListener('pointermove', onPointer);
      window.removeEventListener('pointerout', onPointerLeave);
      window.removeEventListener('pointerdown', onPointerDown);
    };
  }, [height, visible.length]);

  /* ---------------------------
     Bricks canvas: purple→cyan gradient per brick, per-row variation.
     Uses createLinearGradient per brick for nicer gradient feel.
     Also darkens bricks under active panel for text contrast.
     --------------------------- */
  useEffect(() => {
    const canvas = bgCanvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext('2d');
    let W = canvas.width = window.innerWidth;
    let H = canvas.height = window.innerHeight;
    const DPR = Math.min(window.devicePixelRatio || 1, 1.5);
    canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = `${W}px`; canvas.style.height = `${H}px`; ctx.scale(DPR, DPR);

    // brick sizing & grid
    let BRICK_W = window.innerWidth < 640 ? 120 : 96;
    let BRICK_H = window.innerWidth < 640 ? 56 : 44;
    let cols = Math.ceil(W / BRICK_W);
    let rows = Math.ceil(H / BRICK_H);
    const MAX_BRICKS = 900;
    if (cols * rows > MAX_BRICKS) {
      const scaleUp = Math.sqrt((cols * rows) / MAX_BRICKS);
      BRICK_W = Math.ceil(BRICK_W * scaleUp);
      BRICK_H = Math.ceil(BRICK_H * scaleUp);
      cols = Math.ceil(W / BRICK_W); rows = Math.ceil(H / BRICK_H);
    }

    const bricks = [];
    for (let r = 0; r < rows; r++) {
      for (let c = 0; c < cols; c++) {
        const x = c * BRICK_W + (r % 2 ? BRICK_W / 2 : 0);
        const y = r * BRICK_H;
        const cx = x + BRICK_W/2;
        const cy = y + BRICK_H/2;
        bricks.push({ x, y, w: BRICK_W, h: BRICK_H, baseX: x, baseY: y, cx, cy, pop: 0, vy: 0, phase: Math.random()*Math.PI*2, falling: false, fallY:0, fallVy:0, opacity:1, gone:false, r, c });
      }
    }

    canvas._triggerPop = function(px, py){
      let nearest = null; let best = Infinity;
      for (let b of bricks) {
        if (b.gone) continue;
        const dx = b.cx - px; const dy = b.cy - py; const d = Math.sqrt(dx*dx + dy*dy);
        if (d < best) { best = d; nearest = b; }
      }
      if (nearest && !nearest.falling && best < Math.max(120, Math.min(window.innerWidth, window.innerHeight) * 0.06)) {
        nearest.falling = true; nearest.fallVy = Math.random()*4 + 2; nearest.fallY = 0;
      }
      const R = 120;
      for (let b of bricks) {
        if (b.falling || b.gone) continue;
        const dx = b.cx - px; const dy = b.cy - py; const d = Math.sqrt(dx*dx + dy*dy);
        if (d < R) {
          const norm = 1 - d / R;
          b.pop = Math.max(b.pop, 0.3 * norm);
          b.vy += (4 * norm) + (Math.random() * 2);
        }
      }
    };

    // palette
    const palA = hexToRgb(THEME.purple);
    const palB = hexToRgb(THEME.cyan);
    const palC = hexToRgb(THEME.magenta);

    let lastT = performance.now();
    function draw(now) {
      let dt = Math.min(40, now - lastT) * 0.001;
      if (dt <= 0) { bgRAF.current = requestAnimationFrame(draw); return; }
      lastT = now;
      ctx.clearRect(0,0,W,H);

      // translucent wash so background video/fallback shows
      ctx.fillStyle = openingDone ? 'rgba(2,4,10,0.42)' : 'rgba(2,4,10,0.08)';
      ctx.fillRect(0,0,W,H);

      const maxD = Math.max(W,H) * 0.65;
      const time = now * 0.001;
      const skipShadows = bricks.length > 700;

      // compute panel center (so we can darken bricks behind)
      const panelCenterX = W / 2;
      const activeIdx = activeIndexRef.current || 0;
      const panelCenterY = (activeIdx * height - currentY.current) + (height / 2);
      const panelW = Math.min(1120, W * 0.86);
      const panelH = Math.min(760, height * 0.78);

      for (let b of bricks) {
        if (b.gone) continue;
        if (b.falling) {
          b.fallVy += 900 * dt;
          b.fallY += b.fallVy * dt;
          b.opacity = Math.max(0, 1 - (b.fallY / (H * 1.4)));
          if (b.fallY > H + 120) { b.gone = true; continue; }
        }

        const dx = b.cx - cursor.current.x; const dy = b.cy - cursor.current.y;
        const d = Math.sqrt(dx*dx + dy*dy);
        let t = Math.max(0, 1 - d / maxD); t = Math.pow(t, 0.9);
        const lift = b.falling ? 0 : Math.min(14, 10 * t + b.pop * 18);
        const wobble = b.falling ? 0 : Math.sin(b.phase + time * 1.2) * 3 * t;
        if (!b.falling) { b.vy -= 30 * dt; b.vy *= (0.92 - Math.min(0.18, t * 0.12)); b.pop = Math.max(0, b.pop - 0.012); }
        const yOffset = b.falling ? b.fallY : (-lift + b.vy * dt * 60);

        // variation per row/col
        const posFactor = ((b.r % 7) / 7) * 0.2 + ((b.c % 5)/5) * 0.12;
        const anim = 0.45 + 0.55 * Math.sin(b.phase * 1.2 + time * (0.45 + posFactor));
        const mixFactor = Math.min(1, Math.max(0, t * 0.9 + anim * 0.25 + posFactor * 0.12));

        function mix(a,bVal,n){ return Math.round((1-n)*a + n*bVal); }
        const rAB = mix(palA[0], palB[0], mixFactor);
        const gAB = mix(palA[1], palB[1], mixFactor);
        const bAB = mix(palA[2], palB[2], mixFactor);
        const accent = 0.12 * (0.5 + 0.5 * Math.sin(time * 1.8 + b.phase + posFactor));
        let baseR = Math.min(255, Math.round(rAB * (1 - accent) + palC[0] * accent));
        let baseG = Math.min(255, Math.round(gAB * (1 - accent) + palC[1] * accent));
        let baseB = Math.min(255, Math.round(bAB * (1 - accent) + palC[2] * accent));

        // depth
        const depthMul = 0.9 + 0.22 * (1 - (b.cy / H));
        baseR = Math.round(baseR * depthMul); baseG = Math.round(baseG * depthMul); baseB = Math.round(baseB * depthMul);

        // panel darkening when brick is under active panel
        const dxPanel = Math.abs(b.cx - panelCenterX);
        const dyPanel = Math.abs(b.cy - panelCenterY);
        let panelDarkness = 0;
        if (dxPanel < panelW/2 && dyPanel < panelH/2) {
          const nx = 1 - (dxPanel / (panelW/2));
          const ny = 1 - (dyPanel / (panelH/2));
          panelDarkness = Math.pow(Math.max(0, Math.min(1, nx * ny)), 1.4) * 0.66;
        }

        const finalR = Math.max(12, Math.round(baseR * (1 - panelDarkness)));
        const finalG = Math.max(12, Math.round(baseG * (1 - panelDarkness)));
        const finalB = Math.max(12, Math.round(baseB * (1 - panelDarkness)));
        const finalAlpha = Math.max(0.55, b.opacity - (panelDarkness * 0.22));

        // create linear gradient per brick (from top-left to bottom-right)
        const gx = b.baseX + 6; const gy = b.baseY + 6;
        const grad = ctx.createLinearGradient(gx, gy, gx + b.w - 12, gy + b.h - 12);
        const colorStart = `rgba(${Math.max(0,finalR-18)}, ${Math.max(0,finalG-18)}, ${Math.max(0,finalB-18)}, ${finalAlpha})`;
        const colorEnd = `rgba(${finalR}, ${finalG}, ${finalB}, ${finalAlpha})`;
        grad.addColorStop(0, colorStart);
        grad.addColorStop(1, colorEnd);

        ctx.save();
        ctx.globalAlpha = finalAlpha;
        ctx.fillStyle = grad;
        if (lift > 2 && !b.falling && !skipShadows) {
          ctx.shadowBlur = Math.min(28, lift * 1.6);
          ctx.shadowColor = `rgba(40,60,160,${Math.min(0.22, 0.06 * t + 0.06 * b.pop)})`;
        } else ctx.shadowBlur = 0;
        const px = b.baseX + wobble * 0.6;
        const py = b.baseY + yOffset;
        roundRect(ctx, px + 6, py + 6, b.w - 12, b.h - 12, 6);
        ctx.fill();

        // subtle highlight if not heavily darkened
        if ((t > 0.08 || b.pop > 0.02) && !b.falling && panelDarkness < 0.5) {
          ctx.shadowBlur = 0;
          ctx.fillStyle = `rgba(255,255,255,${0.03 * t + 0.04 * b.pop})`;
          ctx.fillRect(px + 12, py + 10, b.w - 24, Math.max(3, Math.min(7, 4 * t + 6 * b.pop)));
        }
        ctx.restore();
      }

      bgRAF.current = requestAnimationFrame(draw);
    }

    function roundRect(ctx,x,y,w,h,r){ const minr = Math.min(r, w/2, h/2); ctx.beginPath(); ctx.moveTo(x+minr,y); ctx.arcTo(x+w,y,x+w,y+h,minr); ctx.arcTo(x+w,y+h,x,y+h,minr); ctx.arcTo(x,y+h,x,y,minr); ctx.arcTo(x,y,x+w,y,minr); ctx.closePath(); }

    function onResize(){ W = canvas.width = window.innerWidth; H = canvas.height = window.innerHeight; canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = `${W}px`; canvas.style.height = `${H}px`; ctx.scale(DPR, DPR); }
    window.addEventListener('resize', onResize);
    bgRAF.current = requestAnimationFrame(draw);
    return () => { window.removeEventListener('resize', onResize); cancelAnimationFrame(bgRAF.current); };
  }, [openingDone, height]);

  // UI frame transform loop
  useEffect(() => {
    function frame(now){
      currentY.current = lerp(currentY.current, targetY.current, (performance.now() - lastWheelAt.current > SNAP_DELAY) ? LERP_SNAP : LERP_FAST);
      const el = containerRef.current; if (el) el.style.transform = `translate3d(0, ${-currentY.current}px, 0)`;

      chapterRefs.current.forEach((node, i) => {
        if(!node) return;
        const center = i * height;
        const offset = (currentY.current - center) / height;
        const abs = Math.abs(offset);
        const translateY = offset * 34;
        const rotate = offset * 3.6;
        const scale = 1 - Math.min(0.08, abs * 0.08);
        const opacity = 1 - Math.min(0.9, abs * 0.9);
        node.style.transform = `translate3d(0px, ${translateY}px, 0) rotate(${rotate}deg) scale(${scale})`;
        node.style.opacity = String(opacity);
      });

      const idx = Math.round(currentY.current / height);
      if (activeIndexRef.current !== idx) { activeIndexRef.current = idx; setActiveIndex(idx); }
      rafRef.current = requestAnimationFrame(frame);
    }
    rafRef.current = requestAnimationFrame(frame);
    return () => cancelAnimationFrame(rafRef.current);
  }, [height, visible.length]);

  function scrollToIndex(i){ const max = Math.max(0, (visible.length - 1) * height); const dest = Math.max(0, Math.min(i * height, max)); targetY.current = dest; lastWheelAt.current = performance.now(); isScrolling.current = true; }

  const AUTO_ADVANCE_DELAY_MS = 240; const MINIGAME_ADVANCE_MS = 300;

  function handleAnswer(qid, value){ setAnswers(prev => ({ ...prev, [qid]: value })); const questionDef = QUESTIONS.find(x => x.id === qid) || {}; let gained = 0; if(questionDef.type === 'mcq' && questionDef.points) gained = questionDef.points[value] || 0; else if(questionDef.type === 'rating') gained = value * (questionDef.pointsPerStar || 1); else if(questionDef.type === 'text') gained = questionDef.points || 0; if(gained){ setScore(s => s + gained); pulseScore(); } if(questionDef.type === 'mcq' || questionDef.type === 'rating'){ const next = Math.min(activeIndexRef.current + 1, visible.length - 1); setTimeout(() => scrollToIndex(next), AUTO_ADVANCE_DELAY_MS); } }

  function pulseScore(){ const el = document.getElementById('score-badge'); if(!el) return; el.animate([{ transform: 'translateY(0) scale(1)' }, { transform: 'translateY(-6px) scale(1.06)' }, { transform: 'translateY(0) scale(1)' }], { duration: 520, easing: 'ease-out' }); }

  function onMiniGameComplete(success){ if(success){ setScore(s => s + 30); pulseScore(); } setAnswers(prev => ({ ...prev, q4: success ? 'success' : 'fail' })); setTimeout(() => scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)), MINIGAME_ADVANCE_MS); }

  function finish(){ setShowFinish(true); if(canvasRef.current) confettiBurst(canvasRef.current); }

  function resetAll(){ if(!window.confirm('Reset survey? This clears local progress.')) return; setAnswers({}); setScore(0); setShowFinish(false); targetY.current = 0; currentY.current = 0; if(containerRef.current) containerRef.current.style.transform = 'translate3d(0,0,0)'; }

  function ChapterContent({ item, idx }){
    if(item.type === 'stage'){
      const isFirst = idx === 0;
      const isLast = idx === Math.max(0, visible.length - 1);
      return (
        <div className="text-center max-w-3xl mx-auto">
          <motion.h1 initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.8 }} style={{ fontSize: '2.5rem', fontWeight: 800, background: `linear-gradient(90deg, ${THEME.purple}, ${THEME.cyan})`, WebkitBackgroundClip: 'text', color: 'transparent', letterSpacing: '-0.01em', textShadow: '0 6px 30px rgba(124,58,237,0.06)' }}>{item.title}</motion.h1>
          {item.subtitle && <motion.p initial={{ y:20, opacity:0 }} animate={{ y:0, opacity:0.95 }} transition={{ delay:0.12 }} style={{ color: THEME.muted, marginTop: 8 }}>{item.subtitle}</motion.p>}

          <div style={{ marginTop: 18, width: '100%', maxWidth: 520, marginLeft: 'auto', marginRight: 'auto', padding: 18, borderRadius: 12, background: THEME.panel, border: '1px solid rgba(255,255,255,0.03)', backdropFilter: 'blur(6px)' }}>
            <div style={{ display: 'flex', gap: 12, alignItems: 'center' }}>
              <svg width="44" height="44" viewBox="0 0 24 24" fill="none"><path d="M12 2l3 7h7l-5.5 4 2 7L12 17l-6.5 3 2-7L2 9h7l3-7z" stroke={THEME.purple} strokeWidth="0.9" /></svg>
              <div style={{ textAlign: 'left' }}>
                <div style={{ color: THEME.text, fontWeight: 700 }}>Experience</div>
                <div style={{ color: THEME.muted, fontSize: 13 }}>A cinematic flow — read the prompt, pick quickly, and earn points.</div>
              </div>
            </div>

            <div style={{ marginTop: 16, display: 'flex', justifyContent: 'center', gap: 12 }}>
              {isLast ? (
                <>
                  <button onClick={() => resetAll()} className="btn btn-primary">Restart</button>
                  <button onClick={() => setShowResponses(true)} className="btn btn-ghost">View responses</button>
                </>
              ) : (
                <>
                  <button onClick={() => scrollToIndex(Math.min(idx + 1, visible.length - 1))} className="btn btn-primary">Enter</button>
                  {!isFirst && <button onClick={() => finish()} className="btn btn-ghost">Skip</button>}
                </>
              )}
            </div>
          </div>
        </div>
      );
    }

    if (item.type === 'mcq') {
      return (
        <div style={{ maxWidth: 980, margin: '0 auto' }}>
          <div style={{ color: THEME.muted, marginBottom: 12 }}>{item.flavor}</div>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))', gap: 14 }}>
            {item.choices.map(c => (
              <motion.button key={c} whileHover={{ scale: 1.03 }} onClick={() => handleAnswer(item.id, c)} style={{ padding: '18px', borderRadius: 22, border: answers[item.id] === c ? 'none' : '1px solid rgba(255,255,255,0.03)', background: answers[item.id] === c ? `linear-gradient(90deg, ${THEME.purple}, ${THEME.cyan})` : 'rgba(255,255,255,0.02)', color: answers[item.id] === c ? 'black' : THEME.text, boxShadow: answers[item.id] === c ? '0 12px 40px rgba(124,58,237,0.12)' : 'none' }}>
                <div style={{ fontWeight: 700, fontSize: 18 }}>{c}</div>
                <div style={{ color: THEME.muted, marginTop: 6 }}>{item.points && item.points[c] ? `${item.points[c]} pts` : ''}</div>
              </motion.button>
            ))}
          </div>
        </div>
      );
    }

    if (item.type === 'rating') {
      return (
        <div style={{ textAlign: 'center' }}>
          <div style={{ color: THEME.muted, marginBottom: 12 }}>{item.flavor}</div>
          <div style={{ display: 'flex', gap: 12, justifyContent: 'center' }}>
            {Array.from({ length: item.scale }).map((_, i) => (
              <motion.button key={i} onClick={() => handleAnswer(item.id, i+1)} whileTap={{ scale: 0.96 }} style={{ width: 64, height: 64, borderRadius: 999, display: 'flex', alignItems: 'center', justifyContent: 'center', background: answers[item.id] === i+1 ? `linear-gradient(90deg, ${THEME.purple}, ${THEME.cyan})` : 'rgba(255,255,255,0.02)', color: answers[item.id] === i+1 ? 'black' : THEME.text, border: '1px solid rgba(255,255,255,0.03)' }}>{i+1}</motion.button>
            ))}
          </div>
        </div>
      );
    }

    if (item.type === 'text') {
      return (
        <div style={{ maxWidth: 980, margin: '0 auto' }}>
          <div style={{ color: THEME.muted, marginBottom: 12 }}>{item.flavor}</div>
          <textarea maxLength={item.maxLength} rows={5} value={answers[item.id] || ''} onChange={(e) => setAnswers(prev => ({ ...prev, [item.id]: e.target.value }))} style={{ width: '100%', padding: 16, borderRadius: 12, background: THEME.panel, border: '1px solid rgba(255,255,255,0.03)', color: THEME.text, resize: 'vertical' }} placeholder="One-line pitch..." />
          <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: 10 }}>
            <div style={{ color: THEME.muted, fontSize: 13 }}>{(answers[item.id] || '').length}/{item.maxLength}</div>
            <div style={{ display: 'flex', gap: 10 }}>
              <button onClick={() => { if (item.points && (answers[item.id] || '').length > 0) { setScore(s => s + item.points); pulseScore(); } scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)); }} className="btn btn-primary">Submit</button>
              <button onClick={() => setAnswers(prev => ({ ...prev, [item.id]: '' }))} className="btn btn-ghost">Clear</button>
            </div>
          </div>
        </div>
      );
    }

    if (item.type === 'minigame') return <MiniGame timeLimit={item.timeLimit} onComplete={onMiniGameComplete} flavor={item.flavor} />;
    return null;
  }

  // progress bar: ensure it reaches 100 when openingDone or fallback
  useEffect(() => {
    if (openingDone) setLoadProgress(100);
  }, [openingDone]);

  // retry UI: user can retry videos
  const showFallbackUI = videoPhase === 'failed';

  return (
    <div style={{ minHeight: '100vh', position: 'relative', overflow: 'hidden', background: THEME.bg, color: THEME.text }}>
      <style>{`
        :root{
          --purple: ${THEME.purple};
          --cyan: ${THEME.cyan};
          --magenta: ${THEME.magenta};
          --text: ${THEME.text};
          --muted: ${THEME.muted};
        }
        .btn { padding: 10px 14px; border-radius: 10px; font-weight: 700; cursor:pointer; border: 1px solid rgba(255,255,255,0.03); }
        .btn-primary { background: linear-gradient(90deg, var(--purple), var(--cyan)); color: black; box-shadow: 0 8px 32px rgba(124,58,237,0.09); }
        .btn-ghost { background: rgba(255,255,255,0.02); color: var(--text); border: 1px solid rgba(255,255,255,0.03); }
      `}</style>

      <CustomCursor />

      {/* video fallback chain:
          - when videoPhase === 'local', try local OPENING_VIDEO_SRC
          - when videoPhase === 'remote', try REMOTE_FALLBACK
          - when videoPhase === 'failed', show AnimatedGradientFallback
      */}
      {videoPhase !== 'failed' ? (
        <VideoBackground
          keyOverride={videoKey + (videoPhase === 'remote' ? '-remote' : '-local')}
          src={videoPhase === 'remote' ? REMOTE_FALLBACK : OPENING_VIDEO_SRC}
          videoRef={videoRef}
          onReady={onVideoReady}
          onError={onVideoError}
        />
      ) : (
        <AnimatedGradientFallback />
      )}

      {/* show helpful notice if videos failed */}
      {showVideoErrorNotice && (
        <div style={{ position: 'fixed', left: 16, bottom: 18, zIndex: 120, background: 'linear-gradient(90deg, rgba(0,0,0,0.5), rgba(0,0,0,0.35))', padding: '10px 12px', borderRadius: 10, border: '1px solid rgba(255,255,255,0.03)' }}>
          <div style={{ color: THEME.text, fontWeight: 700, fontSize: 13 }}>Video failed — using fallback</div>
          <div style={{ color: THEME.muted, marginTop: 6, fontSize: 12, display: 'flex', gap: 8 }}>
            <span>Tip: Put <code style={{ background: 'rgba(255,255,255,0.02)', padding: '2px 6px', borderRadius: 6 }}>{'public/opening.mp4'}</code> in your project and use H.264/AAC for best compatibility.</span>
            <button onClick={retryVideos} className="btn btn-ghost" style={{ padding: '6px 8px', fontSize: 12 }}>Retry</button>
          </div>
        </div>
      )}

      {/* loading overlay */}
      {!openingDone && <LoadingOverlay progress={loadProgress} />}

      {/* canvases */}
      <canvas ref={bgCanvasRef} className="fixed inset-0 z-0 pointer-events-none" />
      <canvas ref={canvasRef} className="fixed inset-0 z-50 pointer-events-none" />

      {/* top-right score */}
      <div style={{ position: 'fixed', right: 18, top: 18, zIndex: 100, display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: 8 }}>
        <div id="score-badge" style={{ padding: '10px 14px', borderRadius: 999, background: `linear-gradient(90deg, ${THEME.purple}, ${THEME.magenta})`, color: '#000', fontWeight: 800, boxShadow: '0 12px 50px rgba(124,58,237,0.12)' }}>{score} pts</div>
        <div style={{ color: THEME.muted, fontSize: 12 }}>Level {Math.floor(score / 50) + 1}</div>
      </div>

      {/* left nav */}
      <nav style={{ position: 'fixed', left: 18, top: '50%', transform: 'translateY(-50%)', zIndex: 100, display: 'flex', flexDirection: 'column', gap: 10 }}>
        {visible.map((_, i) => (
          <button key={i} onClick={() => scrollToIndex(i)} aria-label={`Go to chapter ${i+1}`} style={{ width: 12, height: 12, borderRadius: 999, background: i === activeIndex ? `linear-gradient(90deg, ${THEME.purple}, ${THEME.magenta})` : 'rgba(255,255,255,0.03)', border: '1px solid rgba(255,255,255,0.02)' }} />
        ))}
      </nav>

      {/* main stacked chapters */}
      <div ref={containerRef} className="fixed inset-0 top-0 left-0 w-full will-change-transform z-40">
        <div style={{ height: `${visible.length * height}px` }}>
          {visible.map((item, i) => (
            <section key={item.id} ref={addChapterRef} style={{ height: `${height}px` }} className="flex items-center justify-center px-6 md:px-12">
              <div style={{ width: '100%', maxWidth: 1120, margin: '0 auto', display: 'grid', gridTemplateColumns: '1fr 2fr', gap: 20, alignItems: 'center' }}>
                <aside style={{ display: window.innerWidth >= 1024 ? 'flex' : 'none', flexDirection: 'column', gap: 12, alignItems: 'flex-start' }}>
                  <div style={{ color: THEME.muted, fontSize: 13 }}>Chapter</div>
                  <motion.div animate={{ rotate: activeIndex === i ? 6 : 0 }} transition={{ type: 'spring' }} style={{ fontSize: 36, fontWeight: 900, color: THEME.text }}>{String(i+1).padStart(2,'0')}</motion.div>
                  <div style={{ width: 6, height: '100%', background: `linear-gradient(180deg, ${THEME.purple}, ${THEME.magenta})`, opacity: 0.28, borderRadius: 4 }} />
                </aside>
                <div style={{ borderRadius: 14, padding: 28, textAlign: 'center', background: THEME.panel, border: '1px solid rgba(255,255,255,0.03)', backdropFilter: 'blur(8px)' }}>
                  <motion.div initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.64 }}>
                    <ChapterContent item={item} idx={i} />
                  </motion.div>
                </div>
              </div>
            </section>
          ))}
        </div>
      </div>

      {/* finish modal */}
      <AnimatePresence>{showFinish && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} style={{ position:'fixed', inset: 0, zIndex: 90, display:'flex', alignItems: 'center', justifyContent: 'center' }}>
          <div style={{ width: 'min(720px, 92%)', padding: 28, borderRadius: 18, background: 'rgba(0,0,0,0.6)', border: '1px solid rgba(255,255,255,0.04)', backdropFilter: 'blur(8px)' }}>
            <h2 style={{ color: THEME.text, fontSize: 28, fontWeight: 800 }}>All done 🎉</h2>
            <p style={{ color: THEME.muted, marginTop: 10 }}>You earned <strong style={{ color: THEME.text }}>{score}</strong> points. Thanks for playing!</p>
            <div style={{ marginTop: 18, display: 'flex', justifyContent: 'center' }}>
              <button onClick={() => resetAll()} className="btn btn-primary">Reset</button>
            </div>
          </div>
        </motion.div>
      )}</AnimatePresence>

      {/* responses modal */}
      <AnimatePresence>{showResponses && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} style={{ position:'fixed', inset:0, zIndex: 95, display:'flex', alignItems:'center', justifyContent:'center', padding: 22 }}>
          <div style={{ width: 'min(820px, 94%)', padding: 18, borderRadius: 14, background: 'rgba(0,0,0,0.6)', border: '1px solid rgba(255,255,255,0.03)', backdropFilter: 'blur(8px)' }}>
            <div style={{ display:'flex', justifyContent:'space-between', alignItems:'center' }}>
              <h3 style={{ color: THEME.text, margin:0 }}>Your Responses</h3>
              <button onClick={() => setShowResponses(false)} className="btn btn-ghost" style={{ padding: '8px 10px' }}>Close</button>
            </div>
            <pre style={{ marginTop: 12, maxHeight: '60vh', overflow: 'auto', background: 'rgba(255,255,255,0.02)', padding: 12, borderRadius: 8, color: THEME.text }}>{JSON.stringify(answers, null, 2)}</pre>
            <div style={{ marginTop: 12, display:'flex', justifyContent:'flex-end' }}>
              <button onClick={() => { setShowResponses(false); resetAll(); }} className="btn btn-primary">Restart</button>
            </div>
          </div>
        </motion.div>
      )}</AnimatePresence>

      {/* If both videos failed, show retry prompt near center */}
      {showFallbackUI && (
        <div style={{ position: 'fixed', left: 0, right: 0, bottom: 28, display: 'flex', justifyContent: 'center', zIndex: 110 }}>
          <div style={{ padding: 12, borderRadius: 12, background: 'rgba(0,0,0,0.45)', border: '1px solid rgba(255,255,255,0.03)', display: 'flex', gap: 12, alignItems: 'center' }}>
            <div style={{ color: THEME.text, fontWeight: 700 }}>Video unavailable — using animated fallback</div>
            <button onClick={retryVideos} className="btn btn-primary">Retry Videos</button>
          </div>
        </div>
      )}
    </div>
  );
}
