import React, { useEffect, useState, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";

/**
 * src/App.js — final polish + restored falling bricks & higher reactivity
 * - Restores falling bricks animation (tap/click causes bricks to fall)
 * - Stronger, faster-but-smooth vibration behavior with local influence
 * - Keeps performance caps, precomputed colors and slower cinematic pacing
 */

const THEME = {
  bg: "#05060A",
  card: "rgba(8,12,20,0.72)",
  text: "#E9F8FF",
  muted: "#9FB7C7",
  primary: "#7C3AED",
  cyan: "#06B6D4",
  accent: "#00E5FF",
  warm: "#FFB86B",
};

const MAX_VIBRATE_AMPLITUDE = 11; // slightly higher for punch
const FRAME_CAP = 45;

const QUESTIONS = [
  { id: "intro", type: "stage", title: "Neon Nightfall", subtitle: "Decisions reverberate — pick fast, win faster." },
  { id: "q1", type: "mcq", title: "Vibe Check — choose your drive", choices: ["Prototype & ship", "Learn a trick", "Find teammates", "Prize hunter"], points: { "Prototype & ship": 20, "Learn a trick": 10, "Find teammates": 15, "Prize hunter": 5 }, flavor: "A neon corridor; your choice sets the tone." },
  { id: "q2", type: "rating", title: "Charge Level — how amped are you?", scale: 5, pointsPerStar: 6, flavor: "Lights sync to your excitement." },
  { id: "q3", type: "text", title: "One-line pitch — your spark", maxLength: 140, points: 15, flavor: "A single line. Make it crisp." },
  { id: "q4", type: "minigame", title: "Core Tap — power the idea", timeLimit: 6000, pointsOnSuccess: 30, flavor: "Rapid taps to energize the prototype core." },
  { id: "end", type: "stage", title: "Curtain Call", subtitle: "Badges claimed. Sunlight pending." },
];

function lerp(a,b,n){ return (1-n)*a + n*b }
function hexToRgb(hex){ const h = hex.replace('#',''); return [parseInt(h.substring(0,2),16), parseInt(h.substring(2,4),16), parseInt(h.substring(4,6),16)]; }

function confettiBurst(canvas){ if(!canvas) return; const ctx = canvas.getContext('2d'); const W = canvas.width = window.innerWidth; const H = canvas.height = window.innerHeight; const palette = [THEME.primary, THEME.cyan, THEME.accent, THEME.warm]; const pieces = Array.from({length:50}).map(()=>({ x: W/2 + (Math.random()-0.5)*200, y: H/3 + (Math.random()-0.5)*60, vx:(Math.random()-0.5)*10, vy:(Math.random()-1.6)*8, r:Math.random()*5+2, c:palette[Math.floor(Math.random()*palette.length)], life:80 + Math.random()*80 })); function frame(){ ctx.clearRect(0,0,W,H); let alive=false; for(const p of pieces){ if(p.life>0){ alive=true; p.vy += 0.2; p.x += p.vx; p.y += p.vy; ctx.save(); ctx.translate(p.x,p.y); ctx.fillStyle = p.c; ctx.fillRect(-p.r/2,-p.r/2,p.r,p.r*0.6); ctx.restore(); p.life--; } } if(alive) requestAnimationFrame(frame); else ctx.clearRect(0,0,W,H); } frame(); }

function CustomCursor(){
  const dot = useRef(null), ring = useRef(null);
  const target = useRef({ x: window.innerWidth/2, y: window.innerHeight/2 });
  const hover = useRef(false), raf = useRef(null);

  useEffect(()=>{
    function onMove(e){ target.current.x = e.clientX; target.current.y = e.clientY; }
    function onHover(e){ const t = e.target; hover.current = !!(t.closest && t.closest('button, a, [role="button"], input, textarea')); }
    window.addEventListener('mousemove', onMove); window.addEventListener('mouseover', onHover);

    let px = target.current.x; let py = target.current.y; let ringX = px; let ringY = py; const dotLerp = 0.36; const ringLerp = 0.06; // dot follows faster, ring lags a little less now
    function loop(){ px = lerp(px, target.current.x, dotLerp); py = lerp(py, target.current.y, dotLerp); if(dot.current) dot.current.style.transform = `translate3d(${px-6}px, ${py-6}px, 0)`; ringX = lerp(ringX, target.current.x, ringLerp); ringY = lerp(ringY, target.current.y, ringLerp); if(ring.current){ ring.current.dataset.x = ringX; ring.current.dataset.y = ringY; const scale = hover.current ? 1.85 : 1; ring.current.style.transform = `translate3d(${ringX-18}px, ${ringY-18}px, 0) scale(${scale})`; } raf.current = requestAnimationFrame(loop); }
    raf.current = requestAnimationFrame(loop);
    return ()=>{ window.removeEventListener('mousemove', onMove); window.removeEventListener('mouseover', onHover); cancelAnimationFrame(raf.current); }
  }, []);
  useEffect(()=>{ document.documentElement.classList.add('custom-cursor-hide'); return ()=>document.documentElement.classList.remove('custom-cursor-hide'); }, []);

  return (
    <>
      <div ref={ring} style={{ position:'fixed', left:0, top:0, width:40, height:40, borderRadius:9999, border:'1.6px solid rgba(124,58,237,0.12)', pointerEvents:'none', zIndex:99999, transition:'box-shadow 160ms' }} />
      <div ref={dot} style={{ position:'fixed', left:0, top:0, width:12, height:12, borderRadius:9999, background:'radial-gradient(circle at 30% 30%, #fff, #e6f8ff)', boxShadow:'0 10px 26px rgba(6,182,212,0.08)', pointerEvents:'none', zIndex:100000 }} />
      <style>{`.custom-cursor-hide, .custom-cursor-hide * { cursor: none !important; }`}</style>
    </>
  );
}

function MiniGame({ timeLimit=6000, onComplete, flavor }){
  const [charge, setCharge] = useState(0); const runningRef = useRef(false); const raf = useRef(null); const started = useRef(0); const boost = useRef(0);
  useEffect(()=>()=> cancelAnimationFrame(raf.current), []);
  function start(){ if(runningRef.current) return; runningRef.current = true; boost.current = 0; started.current = performance.now(); tick(); }
  function tick(){ raf.current = requestAnimationFrame(()=>{ const elapsed = performance.now() - started.current; const base = Math.min(100, Math.round((elapsed/timeLimit)*100)); const total = Math.min(100, base + boost.current); setCharge(total); if(total>=100){ runningRef.current=false; cancelAnimationFrame(raf.current); onComplete(true); return; } if(elapsed>=timeLimit){ runningRef.current=false; cancelAnimationFrame(raf.current); onComplete(total>=60); return; } tick(); }); }
  function tap(){ if(!runningRef.current) start(); else { boost.current = Math.min(100, boost.current + 12); setCharge(c=>Math.min(100,c+12)); } }
  return (
    <div style={{ display:'flex', flexDirection:'column', alignItems:'center', gap:12 }}>
      <div style={{ color: THEME.muted, fontSize:13 }}>{flavor}</div>
      <motion.div onClick={tap} whileTap={{ scale:0.94 }} whileHover={{ scale:1.02 }} transition={{ type:'spring', stiffness:300 }} style={{ width:140, height:140, borderRadius:999, display:'flex', alignItems:'center', justifyContent:'center', cursor:'pointer', background:`linear-gradient(135deg, ${THEME.primary}, ${THEME.cyan})`, boxShadow:'0 16px 46px rgba(0,0,0,0.45)' }}>
        <div style={{ fontWeight:900, color:'#06060A' }}>TAP</div>
      </motion.div>
      <div style={{ width:'100%', maxWidth:520 }}>
        <div style={{ color: THEME.muted, marginBottom:6 }}>Charge: {charge}%</div>
        <div style={{ height:12, width:'100%', borderRadius:10, overflow:'hidden', background:'rgba(255,255,255,0.02)' }}>
          <motion.div initial={{ width:0 }} animate={{ width: `${charge}%` }} transition={{ type:'spring', stiffness:120, damping:18 }} style={{ height:'100%', background:`linear-gradient(90deg, ${THEME.cyan}, ${THEME.primary})` }} />
        </div>
      </div>
    </div>
  );
}

export default function GamifiedSurveyForm(){
  useEffect(()=>{ document.title = 'HackPlay — Neon Survey'; }, []);

  const [answers, setAnswers] = useState(()=>{ try{ const r = localStorage.getItem('gamifiedSurveyAnswers'); return r?JSON.parse(r):{} }catch{ return {}; } });
  const [score, setScore] = useState(()=>Number(localStorage.getItem('gamifiedSurveyScore'))||0);
  const [showFinish, setShowFinish] = useState(false);

  const confettiCanvas = useRef(null);
  const bgCanvas = useRef(null);

  const [height, setHeight] = useState(window.innerHeight);
  useEffect(()=>{ function onResize(){ setHeight(window.innerHeight); } window.addEventListener('resize', onResize); return ()=>window.removeEventListener('resize', onResize); }, []);

  useEffect(()=>localStorage.setItem('gamifiedSurveyAnswers', JSON.stringify(answers)), [answers]);
  useEffect(()=>localStorage.setItem('gamifiedSurveyScore', String(score)), [score]);

  const visible = QUESTIONS;
  const containerRef = useRef(null); const chapterRefs = useRef([]); chapterRefs.current = [];
  const addRef = el => { if(el) chapterRefs.current.push(el) };
  const targetY = useRef(0); const currentY = useRef(0); const rafUI = useRef(null);
  const pageIndexRef = useRef(0); const [activeIndex, setActiveIndex] = useState(0);

  const cursor = useRef({ x: window.innerWidth/2, y: window.innerHeight/2 });
  useEffect(()=>{ function onPointer(e){ cursor.current.x = e.clientX; cursor.current.y = e.clientY; } window.addEventListener('pointermove', onPointer); return ()=>window.removeEventListener('pointermove', onPointer); }, []);

  // loader: 3s
  const [loading, setLoading] = useState(true); const [progress, setProgress] = useState(0);
  useEffect(()=>{ let raf = null; const start = performance.now(); const D = 3000; function tick(now){ const t = Math.min(1,(now-start)/D); setProgress(Math.round(100 * t)); if(t<1) raf = requestAnimationFrame(tick); else setTimeout(()=> setLoading(false), 240); } raf = requestAnimationFrame(tick); return ()=> cancelAnimationFrame(raf); }, []);

  // wheel -> page snap
  useEffect(()=>{
    let acc = 0; let lock = false; function clamp(i){ return Math.max(0, Math.min(i, visible.length-1)); }
    function setPage(i){ const c = clamp(i); pageIndexRef.current = c; targetY.current = c * height; setActiveIndex(c); }
    function onWheel(e){ if(loading) return; e.preventDefault(); acc += e.deltaY; const TH = 80; if(Math.abs(acc) > TH && !lock){ const dir = acc>0?1:-1; setPage(pageIndexRef.current + dir); acc = 0; lock = true; setTimeout(()=> lock = false, 320); } }
    window.addEventListener('wheel', onWheel, { passive:false }); return ()=> window.removeEventListener('wheel', onWheel);
  }, [height, loading, visible.length]);

  // UI lerp loop: cinematic
  useEffect(()=>{ function loop(){ currentY.current = lerp(currentY.current, targetY.current, 0.16); if(containerRef.current) containerRef.current.style.transform = `translate3d(0, ${-currentY.current}px, 0)`; chapterRefs.current.forEach((node, i)=>{ if(!node) return; const center = i * height; const offset = (currentY.current - center)/height; const abs = Math.abs(offset); const translateY = offset * 12; const rotate = offset * 1.6; const scale = 1 - Math.min(0.06, abs * 0.06); const opacity = 1 - Math.min(0.9, abs * 0.9); node.style.transform = `translate3d(0px, ${translateY}px, 0) rotate(${rotate}deg) scale(${scale})`; node.style.opacity = String(opacity); }); const idx = Math.round(currentY.current / height); if(activeIndex !== idx) setActiveIndex(idx); rafUI.current = requestAnimationFrame(loop); } rafUI.current = requestAnimationFrame(loop); return ()=> cancelAnimationFrame(rafUI.current); }, [height]);

  // ---------------- Bricks canvas with falling and reactive pop ----------------
  useEffect(()=>{
    const canvas = bgCanvas.current; if(!canvas) return; const ctx = canvas.getContext('2d'); let W = window.innerWidth, H = window.innerHeight; const DPR = Math.min(window.devicePixelRatio || 1, 1.2);
    canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = `${W}px`; canvas.style.height = `${H}px`; ctx.scale(DPR, DPR);

    let BRW = window.innerWidth < 640 ? 120 : 96; let BRH = window.innerWidth < 640 ? 56 : 44; let cols = Math.ceil(W/BRW), rows = Math.ceil(H/BRH);
    const MAX = 700; if(cols*rows > MAX){ const scaleUp = Math.sqrt((cols*rows)/MAX); BRW = Math.ceil(BRW * scaleUp); BRH = Math.ceil(BRH * scaleUp); cols = Math.ceil(W/BRW); rows = Math.ceil(H/BRH); }

    const bricks = []; const purple = hexToRgb(THEME.primary); const cyan = hexToRgb(THEME.cyan);
    for(let r=0;r<rows;r++){
      for(let c=0;c<cols;c++){
        const x = c*BRW + (r%2?BRW/2:0); const y = r*BRH; const cx = x + BRW/2; const cy = y + BRH/2;
        const mix = Math.random(); const r0 = Math.round((1-mix)*purple[0] + mix*cyan[0]); const g0 = Math.round((1-mix)*purple[1] + mix*cyan[1]); const b0 = Math.round((1-mix)*purple[2] + mix*cyan[2]);
        bricks.push({ x, y, w:BRW, h:BRH, cx, cy, baseX:x, baseY:y, phase:Math.random()*Math.PI*2, vy:0, pop:0, gone:false, falling:false, fallVy:0, fallY:0, opacity:1, col:[r0,g0,b0], mix });
      }
    }

    // trigger pop / fall from pointer coordinates
    canvas._triggerPop = function(px, py){
      // explode nearest and add pop to neighbours
      let nearest = null; let best = Infinity;
      for(const b of bricks){ if(b.gone) continue; const dx = b.cx - px; const dy = b.cy - py; const d = Math.hypot(dx,dy); if(d < best){ best = d; nearest = b; } }
      if(nearest && best < Math.max(120, Math.min(W,H) * 0.06) && !nearest.falling){ nearest.falling = true; nearest.fallVy = 200 + Math.random()*220; nearest.fallY = 0; }
      const R = 140;
      for(const b of bricks){ if(b.gone || b.falling) continue; const dx = b.cx - px; const dy = b.cy - py; const d = Math.hypot(dx,dy); if(d < R){ const norm = 1 - d / R; b.pop = Math.max(b.pop, 0.4 * norm + 0.02); b.vy += (6 * norm) + (Math.random()*3); }
      }
    };

    let lastT = performance.now(); const FRAME_MS = 1000 / FRAME_CAP; let raf = null; const startTime = performance.now();
    function draw(now){ if(now - lastT < FRAME_MS){ raf = requestAnimationFrame(draw); return; } const dt = Math.min(40, now - lastT) * 0.001; lastT = now; ctx.clearRect(0,0,W,H);

      // background wash for readability
      ctx.fillStyle = 'rgba(4,6,10,0.46)'; ctx.fillRect(0,0,W,H);

      const time = now * 0.001; const maxD = Math.max(W,H) * 0.6; const activeCenterX = W/2; const activeCenterY = (pageIndexRef.current * height - currentY.current) + (height/2);
      const elapsedSec = Math.max(0, (now - startTime) / 1000);
      const speedFactor = 1 + Math.min(1, elapsedSec / 50) * 0.4; // ramp slower

      for(const b of bricks){ if(b.gone) continue;
        // falling bricks: physics to drop away with rotation and fade
        if(b.falling){ b.fallVy += 900 * dt; b.fallY += b.fallVy * dt; b.opacity = Math.max(0, 1 - (b.fallY / (H * 1.2))); if(b.fallY > H + 120){ b.gone = true; continue; } }

        const dx = b.cx - cursor.current.x; const dy = b.cy - cursor.current.y; const d = Math.hypot(dx,dy);
        let t = Math.max(0, 1 - d / maxD); t = Math.pow(t, 0.95);

        // organic motion
        b.vy *= 0.92; b.vy -= 20 * dt * t; b.pop = Math.max(0, b.pop - 0.018);
        const lift = b.falling ? 0 : Math.min(18, 12 * t + b.pop * 22);

        // wobble + local vibrate: faster and more pronounced near cursor or active page center
        const wobble = b.falling ? 0 : Math.sin(b.phase + time * (1.2 * speedFactor)) * (1.4 + 1.6 * b.mix) * t;
        const vibInfluence = Math.max(0, 1 - Math.hypot(b.cx - activeCenterX, b.cy - activeCenterY) / Math.max(W,H));
        const vibAmp = MAX_VIBRATE_AMPLITUDE * (0.8 + 0.4 * b.mix) * vibInfluence; // per-brick amplitude
        const vibrate = (b.falling ? 0 : Math.sin(time * (1.8 * speedFactor + (b.phase % 1))) * vibAmp * (0.7 + t));

        const px = b.baseX + wobble + vibrate;
        const py = b.baseY - lift + b.vy * dt * 50 + (b.falling ? b.fallY : 0);

        // color pulse
        const r = Math.max(6, Math.min(255, Math.round(b.col[0] + Math.sin(time*0.6 + b.phase)*6)));
        const g = Math.max(6, Math.min(255, Math.round(b.col[1] + Math.cos(time*0.62 + b.phase)*6)));
        const bb = Math.max(6, Math.min(255, Math.round(b.col[2] + Math.sin(time*0.64 + b.phase)*6)));
        const alpha = (b.opacity !== undefined ? b.opacity : 1) * (0.96 - vibInfluence*0.12);

        const grad = ctx.createLinearGradient(px+6,py+6,px + b.w - 6, py + b.h - 6);
        grad.addColorStop(0, `rgba(${Math.max(0,r-18)}, ${Math.max(0,g-18)}, ${Math.max(0,bb-18)}, ${alpha})`);
        grad.addColorStop(1, `rgba(${r}, ${g}, ${bb}, ${alpha})`);
        ctx.save();
        // subtle rotate for falling bricks
        if(b.falling){ ctx.translate(px + b.w/2, py + b.h/2); ctx.rotate((b.phase % 1) * (b.fallY / (H/3)) ); ctx.translate(-(px + b.w/2), -(py + b.h/2)); }
        ctx.fillStyle = grad; roundRect(ctx, px+6, py+6, b.w - 12, b.h - 12, 6); ctx.fill();
        // highlight
        if(t > 0.06 && !b.falling) { ctx.fillStyle = `rgba(255,255,255,${0.025 * t + 0.02 * b.pop})`; ctx.fillRect(px+12, py+10, b.w - 24, Math.max(2, Math.min(6, 3 * t))); }
        ctx.restore();
      }

      raf = requestAnimationFrame(draw);
    }

    function roundRect(ctx,x,y,w,h,r){ const minr = Math.min(r, w/2, h/2); ctx.beginPath(); ctx.moveTo(x+minr,y); ctx.arcTo(x+w,y,x+w,y+h,minr); ctx.arcTo(x+w,y+h,x,y+h,minr); ctx.arcTo(x,y+h,x,y,minr); ctx.arcTo(x,y,x+ w,y,minr); ctx.closePath(); }

    raf = requestAnimationFrame(draw);

    // pointerdown triggers pop/fall
    function onPointerDown(e){ const rect = canvas.getBoundingClientRect(); const px = e.clientX - rect.left; const py = e.clientY - rect.top; canvas._triggerPop(px, py); }
    window.addEventListener('pointerdown', onPointerDown);

    function onResize(){ W = window.innerWidth; H = window.innerHeight; canvas.width = W * DPR; canvas.height = H * DPR; canvas.style.width = `${W}px`; canvas.style.height = `${H}px`; ctx.scale(DPR,DPR); }
    window.addEventListener('resize', onResize);

    return ()=>{ window.removeEventListener('resize', onResize); window.removeEventListener('pointerdown', onPointerDown); cancelAnimationFrame(raf); };
  }, [height]);

  // helpers
  function scrollToIndex(i){ const max = Math.max(0, (visible.length - 1) * height); const dest = Math.max(0, Math.min(i * height, max)); targetY.current = dest; pageIndexRef.current = Math.round(dest / height); }
  function handleAnswer(qid, value){ setAnswers(prev => ({ ...prev, [qid]: value })); const q = QUESTIONS.find(x=>x.id===qid) || {}; let gained = 0; if(q.type==='mcq' && q.points) gained = q.points[value] || 0; else if(q.type==='rating') gained = value * (q.pointsPerStar||1); else if(q.type==='text') gained = q.points||0; if(gained) setScore(s=>s+gained); if(q.type==='mcq' || q.type==='rating'){ setTimeout(()=> scrollToIndex(pageIndexRef.current + 1), 260); } }
  function pulseScore(){ const el = document.getElementById('score-badge'); if(!el) return; el.animate([{ transform:'translateY(0) scale(1)' },{ transform:'translateY(-6px) scale(1.04)' },{ transform:'translateY(0) scale(1)' }], { duration:520, easing:'ease-out' }); }
  function finish(){ setShowFinish(true); if(confettiCanvas.current) confettiBurst(confettiCanvas.current); }

  return (
    <div style={{ minHeight:'100vh', background:THEME.bg, color:THEME.text, position:'relative', overflow:'hidden' }}>
      <style>{`
        .btn { padding:10px 14px; border-radius:12px; font-weight:800; cursor:pointer; border:1px solid rgba(255,255,255,0.03); transition: transform 160ms ease, box-shadow 160ms ease; }
        .btn-primary { background: linear-gradient(90deg, ${THEME.cyan}, ${THEME.primary}); color:#02171A; box-shadow:0 12px 40px rgba(6,182,212,0.06); }
        .btn-primary:hover { transform: translateY(-4px); box-shadow:0 26px 64px rgba(6,182,212,0.12); }
        .btn-ghost { background: rgba(255,255,255,0.02); color:${THEME.text}; }
        .heading-glow { background-image: linear-gradient(90deg, ${THEME.cyan}, ${THEME.primary}); -webkit-background-clip: text; color: transparent; }
        @keyframes subtleGlow { 0%{ filter:brightness(1) } 50%{ filter:brightness(1.06) } 100%{ filter:brightness(1) } }
      `}</style>

      <CustomCursor />
      <canvas ref={bgCanvas} className="fixed inset-0 z-0 pointer-events:none" style={{ filter: loading ? 'blur(8px) brightness(0.55)' : 'none', transition: 'filter 420ms ease' }} />
      <canvas ref={confettiCanvas} className="fixed inset-0 z-40 pointer-events:none" />

      {loading && <div style={{ position:'fixed', inset:0, zIndex:118, background:'rgba(3,4,10,0.42)', backdropFilter:'blur(6px)' }} />}

      <div style={{ position:'fixed', left:16, top:16, zIndex:80, display:'flex', gap:12, alignItems:'center' }}>
        <div id="score-badge" style={{ padding:'10px 14px', borderRadius:9999, background:'linear-gradient(90deg, rgba(6,182,212,0.1), rgba(124,58,237,0.08))', fontWeight:900 }}>{score} pts</div>
      </div>

      {loading && (
        <div style={{ position:'fixed', inset:0, zIndex:120, display:'flex', alignItems:'center', justifyContent:'center', pointerEvents:'none' }}>
          <div style={{ textAlign:'center' }}>
            <div style={{ fontWeight:900, marginBottom:14, fontSize:18 }}>Loading — Neon Calibration</div>
            <div style={{ display:'flex', gap:10, justifyContent:'center', alignItems:'center' }}>
              <div style={{ width:36, height:80, borderRadius:8, background:`linear-gradient(180deg, rgba(6,182,212,0.14), rgba(124,58,237,0.08))`, transform:'skewX(-6deg)', animation:'pulse 1.1s infinite' }} />
              <div style={{ width:36, height:100, borderRadius:8, background:`linear-gradient(180deg, rgba(124,58,237,0.18), rgba(6,182,212,0.06))`, transform:'skewX(-4deg)', animation:'pulse 1.1s infinite .08s' }} />
              <div style={{ width:36, height:80, borderRadius:8, background:`linear-gradient(180deg, rgba(0,229,255,0.12), rgba(124,58,237,0.04))`, transform:'skewX(-2deg)', animation:'pulse 1.1s infinite .14s' }} />
            </div>
            <div style={{ width:420, height:12, borderRadius:999, background:'rgba(255,255,255,0.02)', margin:'14px auto', overflow:'hidden' }}>
              <div style={{ height:'100%', width:`${progress}%`, background:`linear-gradient(90deg, ${THEME.cyan}, ${THEME.primary})`, transition:'width 220ms linear' }} />
            </div>
            <div style={{ color:THEME.muted, marginTop:8 }}>{progress}%</div>
          </div>
        </div>
      )}

      <div ref={containerRef} className="fixed inset-0 z-10" style={{ willChange:'transform' }}>
        <div style={{ height:`${visible.length * height}px` }}>
          {visible.map((item, i) => (
            <section key={item.id} ref={addRef} style={{ height:`${height}px`, display:'flex', alignItems:'center', justifyContent:'center', padding:20 }}>
              <div style={{ width:'100%', maxWidth:1100, display:'grid', gridTemplateColumns:'280px 1fr', gap:20, alignItems:'center' }}>

                <aside style={{ display: window.innerWidth >= 1000 ? 'block' : 'none' }}>
                  <div style={{ position:'sticky', top:36 }}>
                    <div style={{ background:THEME.card, padding:16, borderRadius:14, boxShadow:'0 18px 60px rgba(2,6,12,0.6)', backdropFilter:'blur(6px)', border:'1px solid rgba(255,255,255,0.03)' }}>
                      <div style={{ color:THEME.muted }}>Chapter</div>
                      <div style={{ fontSize:22, fontWeight:900, marginTop:8 }}>{String(i+1).padStart(2,'0')}</div>
                      <div style={{ color:THEME.muted, marginTop:6 }}>{item.title}</div>
                      <div style={{ marginTop:12, display:'flex', gap:8 }}>
                        <button onClick={()=> scrollToIndex(i-1)} className="btn btn-ghost">Prev</button>
                        <button onClick={()=> scrollToIndex(i+1)} className="btn btn-primary">Next</button>
                      </div>
                    </div>
                  </div>
                </aside>

                <div style={{ borderRadius:14, padding:24, background:THEME.card, minHeight:260, display:'flex', alignItems:'center', justifyContent:'center', boxShadow:'0 24px 80px rgba(8,6,20,0.6)', backdropFilter:'blur(6px)', border:'1px solid rgba(255,255,255,0.03)' }}>
                  <div style={{ width:'100%', maxWidth:780 }}>
                    {item.type === 'stage' && (
                      <div style={{ textAlign:'center' }}>
                        <h1 className="heading-glow" style={{ fontSize:32, fontWeight:900, animation:'subtleGlow 2.8s ease-in-out infinite' }}>{item.title}</h1>
                        {item.subtitle && <div style={{ color:THEME.muted, marginTop:8 }}>{item.subtitle}</div>}
                        <div style={{ marginTop:12 }}>
                          <button onClick={()=> scrollToIndex(i+1)} className="btn btn-primary">Enter</button>
                        </div>
                      </div>
                    )}

                    {item.type === 'mcq' && (
                      <div>
                        <div style={{ color:THEME.muted, marginBottom:12 }}>{item.flavor}</div>
                        <div style={{ display:'grid', gridTemplateColumns:'repeat(auto-fit, minmax(220px,1fr))', gap:12 }}>
                          {item.choices.map(c => (
                            <button key={c} onClick={()=> handleAnswer(item.id, c)} style={{ padding:16, borderRadius:12, textAlign:'left', background:'linear-gradient(180deg, rgba(255,255,255,0.02), rgba(0,0,0,0.02))', border:'1px solid rgba(255,255,255,0.02)', boxShadow:'0 12px 40px rgba(6,18,32,0.36)' }}>
                              <div style={{ fontWeight:800 }}>{c}</div>
                              <div style={{ color:THEME.muted, marginTop:6 }}>{item.points && item.points[c] ? `${item.points[c]} pts` : ''}</div>
                            </button>
                          ))}
                        </div>
                      </div>
                    )}

                    {item.type === 'rating' && (
                      <div style={{ textAlign:'center' }}>
                        <div style={{ color:THEME.muted, marginBottom:12 }}>{item.flavor}</div>
                        <div style={{ display:'flex', gap:10, justifyContent:'center' }}>
                          {Array.from({ length: item.scale }).map((_,idx)=> (
                            <button key={idx} onClick={()=> handleAnswer(item.id, idx+1)} style={{ width:56, height:56, borderRadius:999, background:'rgba(255,255,255,0.02)', border:'1px solid rgba(255,255,255,0.03)', fontWeight:800 }}>{idx+1}</button>
                          ))}
                        </div>
                      </div>
                    )}

                    {item.type === 'text' && (
                      <div>
                        <div style={{ color:THEME.muted, marginBottom:8 }}>{item.flavor}</div>
                        <textarea rows={4} maxLength={item.maxLength} value={answers[item.id]||''} onChange={(e)=> setAnswers(prev=>({ ...prev, [item.id]: e.target.value }))} placeholder="One-line pitch..." style={{ width:'100%', padding:12, borderRadius:8, background:THEME.card, color:THEME.text }} />
                        <div style={{ display:'flex', justifyContent:'space-between', marginTop:8 }}>
                          <div style={{ color:THEME.muted }}>{(answers[item.id]||'').length}/{item.maxLength}</div>
                          <div style={{ display:'flex', gap:8 }}>
                            <button onClick={()=> { if(item.points && (answers[item.id]||'').length>0) setScore(s=>s+item.points); scrollToIndex(pageIndexRef.current+1); }} className="btn btn-primary">Submit</button>
                            <button onClick={()=> setAnswers(prev=>({ ...prev, [item.id]: '' }))} className="btn btn-ghost">Clear</button>
                          </div>
                        </div>
                      </div>
                    )}

                    {item.type === 'minigame' && <MiniGame timeLimit={item.timeLimit} onComplete={(s)=>{ if(s) setScore(x=>x+item.pointsOnSuccess); scrollToIndex(pageIndexRef.current+1); }} flavor={item.flavor} />}

                  </div>
                </div>

              </div>
            </section>
          ))}
        </div>
      </div>

      <AnimatePresence>{showFinish && (
        <motion.div initial={{ opacity:0 }} animate={{ opacity:1 }} exit={{ opacity:0 }} style={{ position:'fixed', inset:0, zIndex:140, display:'flex', alignItems:'center', justifyContent:'center' }}>
          <div style={{ width:640, padding:20, borderRadius:10, background:THEME.card, boxShadow:'0 18px 60px rgba(2,6,12,0.6)', backdropFilter:'blur(6px)' }}>
            <h3 style={{ fontSize:22, fontWeight:900 }}>Done — Results</h3>
            <p style={{ color:THEME.muted }}>You scored <strong style={{ color:THEME.text }}>{score}</strong>. Nice work!</p>
            <div style={{ display:'flex', gap:8, marginTop:12 }}>
              <button onClick={()=> { setShowFinish(false); }} className="btn btn-ghost">Close</button>
              <button onClick={()=> { setShowFinish(false); if(confettiCanvas.current) confettiBurst(confettiCanvas.current); }} className="btn btn-primary">Celebrate</button>
            </div>
          </div>
        </motion.div>
      )}</AnimatePresence>

    </div>
  );
}
