import React, { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";

const chapters = [
  {
    id: "intro",
    type: "info",
    title: "Welcome to the Hackathon Survey!",
    description:
      "This is not your boring Google Form. Scroll down or tap through cinematic survey chapters. Let's go 🚀",
  },
  {
    id: "mcq1",
    type: "mcq",
    title: "Choose your coding superpower ⚡",
    options: ["Frontend Magic", "Backend Strength", "Data Wizardry", "AI Genius"],
  },
  {
    id: "rating1",
    type: "rating",
    title: "Rate your hackathon excitement 🔥",
    scale: 5,
  },
  {
    id: "minigame",
    type: "tap",
    title: "Charge up your hackathon energy! ⚡ Tap fast!",
  },
  {
    id: "text",
    type: "text",
    title: "Your one-line hackathon pitch 🎤",
    placeholder: "Write something epic...",
  },
  {
    id: "end",
    type: "info",
    title: "You're all set! 🎉",
    description: "Thanks for playing through the survey. Get ready to hack and have fun!",
  },
];

export default function GamifiedSurvey() {
  const [answers, setAnswers] = useState({});
  const [score, setScore] = useState(0);
  const [activeIndex, setActiveIndex] = useState(0);
  const [showFinish, setShowFinish] = useState(false);

  const containerRef = useRef(null);
  const targetY = useRef(0);
  const currentY = useRef(0);

  // Smooth scroll simulation
  useEffect(() => {
    const step = () => {
      currentY.current += (targetY.current - currentY.current) * 0.08;
      if (containerRef.current) {
        containerRef.current.style.transform = `translate3d(0,${-currentY.current}px,0)`;
      }
      requestAnimationFrame(step);
    };
    step();
  }, []);

  const handleScroll = (e) => {
    const direction = e.deltaY > 0 ? 1 : -1;
    const nextIndex = Math.min(Math.max(activeIndex + direction, 0), chapters.length - 1);
    setActiveIndex(nextIndex);
    targetY.current = nextIndex * window.innerHeight;
  };

  useEffect(() => {
    window.addEventListener("wheel", handleScroll, { passive: true });
    return () => window.removeEventListener("wheel", handleScroll);
  }, [activeIndex]);

  function saveAnswer(id, value) {
    setAnswers((prev) => ({ ...prev, [id]: value }));
    setScore((prev) => prev + 10);
    if (id !== "text") {
      setTimeout(() => {
        const nextIndex = Math.min(activeIndex + 1, chapters.length - 1);
        setActiveIndex(nextIndex);
        targetY.current = nextIndex * window.innerHeight;
        if (chapters[nextIndex].id === "end") setShowFinish(true);
      }, 240);
    }
  }

  function resetAll() {
    if (!window.confirm("Reset survey? This clears local progress.")) return;
    setAnswers({});
    setScore(0);
    setShowFinish(false);
    localStorage.removeItem("gamifiedSurveyAnswers");
    localStorage.removeItem("gamifiedSurveyScore");
    targetY.current = 0;
    currentY.current = 0;
    if (containerRef.current) containerRef.current.style.transform = "translate3d(0,0,0)";
  }

  return (
    <div className="w-screen h-screen bg-black text-white overflow-hidden">
      <div ref={containerRef} className="will-change-transform">
        {chapters.map((ch, i) => (
          <section
            key={ch.id}
            className="w-screen h-screen flex flex-col items-center justify-center relative"
          >
            <div
              className={`absolute top-0 left-0 w-full h-2 transition-opacity duration-700 ${
                i === activeIndex ? "opacity-100 bg-gradient-to-r from-purple-500 to-pink-500" : "opacity-0"
              }`}
            ></div>
            <motion.div
              className={`p-8 rounded-2xl shadow-xl text-center max-w-2xl mx-auto ${
                i === activeIndex
                  ? "border-l-8 border-purple-500 bg-black/70 backdrop-blur"
                  : "bg-black/50"
              }`}
              initial={{ opacity: 0, y: 80 }}
              animate={{
                opacity: i === activeIndex ? 1 : 0.3,
                y: i === activeIndex ? 0 : 50,
              }}
              transition={{ duration: 0.6 }}
            >
              <h2 className="text-3xl font-bold mb-4">{ch.title}</h2>
              {ch.type === "info" && <p>{ch.description}</p>}
              {ch.type === "mcq" && (
                <div className="flex flex-col space-y-3">
                  {ch.options.map((opt) => (
                    <button
                      key={opt}
                      onClick={() => saveAnswer(ch.id, opt)}
                      className="px-6 py-3 rounded-lg bg-gradient-to-r from-purple-600 to-pink-600 hover:scale-105 transition-transform"
                    >
                      {opt}
                    </button>
                  ))}
                </div>
              )}
              {ch.type === "rating" && (
                <div className="flex justify-center space-x-2">
                  {Array.from({ length: ch.scale }).map((_, idx) => (
                    <button
                      key={idx}
                      onClick={() => saveAnswer(ch.id, idx + 1)}
                      className="text-2xl hover:scale-125 transition-transform"
                    >
                      ⭐
                    </button>
                  ))}
                </div>
              )}
              {ch.type === "tap" && <TapGame onComplete={(val) => saveAnswer(ch.id, val)} />}
              {ch.type === "text" && (
                <TextPage
                  onSubmit={(val) => saveAnswer(ch.id, val)}
                  placeholder={ch.placeholder}
                />
              )}
            </motion.div>
          </section>
        ))}
      </div>

      {/* Progress dots */}
      <div className="fixed right-4 top-1/2 transform -translate-y-1/2 space-y-2">
        {chapters.map((_, idx) => (
          <div
            key={idx}
            className={`w-3 h-3 rounded-full ${
              idx === activeIndex ? "bg-pink-500" : "bg-gray-600"
            }`}
          ></div>
        ))}
      </div>

      {/* Finish screen */}
      <AnimatePresence>
        {showFinish && (
          <motion.div
            className="fixed inset-0 bg-black/90 flex flex-col items-center justify-center z-50"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
          >
            <h1 className="text-4xl font-bold mb-4">Survey Complete 🎉</h1>
            <p className="mb-6">Your Score: {score}</p>
            <button
              onClick={resetAll}
              className="px-6 py-3 bg-gradient-to-r from-purple-500 to-pink-500 rounded-lg"
            >
              Restart
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

function TapGame({ onComplete }) {
  const [charge, setCharge] = useState(0);

  useEffect(() => {
    let interval = setInterval(() => {
      setCharge((c) => {
        if (c >= 100) {
          clearInterval(interval);
          onComplete("Fully charged!");
          return c;
        }
        return c + 1;
      });
    }, 200);
    return () => clearInterval(interval);
  }, [onComplete]);

  return (
    <div>
      <p className="mb-4">Tap to charge! ({Math.min(charge, 100)}%)</p>
      <button
        onClick={() => setCharge((c) => Math.min(c + 10, 100))}
        className="px-8 py-4 bg-purple-600 rounded-lg hover:scale-105 transition-transform"
      >
        ⚡ Tap ⚡
      </button>
    </div>
  );
}

function TextPage({ onSubmit, placeholder }) {
  const [val, setVal] = useState("");
  return (
    <div className="flex flex-col items-center space-y-4">
      <input
        value={val}
        onChange={(e) => setVal(e.target.value)}
        placeholder={placeholder}
        className="px-4 py-2 rounded-lg text-black w-72"
      />
      <div className="flex space-x-2">
        <button
          onClick={() => {
            if (val.trim()) onSubmit(val);
          }}
          className="px-6 py-2 bg-pink-500 rounded-lg"
        >
          Submit
        </button>
        <button
          onClick={() => setVal("")}
          className="px-6 py-2 bg-gray-500 rounded-lg"
        >
          Clear
        </button>
      </div>
    </div>
  );
}
