import React, { useEffect, useState, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";

/**
 * Gamified Survey — unified neon theme, robust video fallback, creative flourishes.
 * - Put your video in public/opening.mp4 or change OPENING_VIDEO_SRC below.
 * - If the local video fails, the app offers retry and a remote fallback (MDN sample).
 */

// ====== CONFIG: set your opening video path here ======
const OPENING_VIDEO_SRC = "/opening.mp4"; // <-- confirm public/opening.mp4 or change this path
const REMOTE_FALLBACK = "https://interactive-examples.mdn.mozilla.net/media/cc0-videos/flower.mp4"; // reliable test fallback

// ====== THEME COLORS (hex). The CSS below uses these variables. ======
const THEME = {
  indigo: "#8b5cf6",
  cyan: "#06b6d4",
  accent: "#ec4899",
  background: "#050612",
  panel: "rgba(6,8,22,0.6)",
  mutedText: "#9aa6b2",
  text: "#e6eef8",
};

const QUESTIONS = [
  { id: "intro", type: "stage", title: "Nightfall at HackPlay", subtitle: "Step into a neon-lit hall. Your choices tune the journey." },
  {
    id: "q1",
    type: "mcq",
    title: "Entrance — choose your mindset",
    choices: ["Build something cool", "Learn fast", "Meet people", "Chase the prizes"],
    points: { "Build something cool": 20, "Learn fast": 10, "Meet people": 15, "Chase the prizes": 5 },
    flavor: "The doors close. A low bass. Pick the vibe that follows you.",
  },
  {
    id: "q2",
    type: "rating",
    title: "Charge Meter — how hyped are you?",
    scale: 5,
    pointsPerStar: 6,
    flavor: "The stage lights react to your energy.",
  },
  {
    id: "q3",
    type: "text",
    title: "One-line pitch — what's the idea?",
    maxLength: 140,
    points: 15,
    flavor: "A single line — the fragment your team will build around.",
  },
  {
    id: "q4",
    type: "minigame",
    title: "Charge the core — tap to power your idea",
    timeLimit: 6000,
    pointsOnSuccess: 30,
    flavor: "Fast reflexes prove prototype-ready energy.",
  },
  {
    id: "q5",
    type: "mcq",
    title: "Team vibe — pick your tribe",
    choices: ["Chill coders", "All-nighters", "Balanced squad", "Solo hero"],
    points: { "Chill coders": 10, "All-nighters": 20, "Balanced squad": 15, "Solo hero": 5 },
    flavor: "Who will you ride the night with?",
    dependsOn: { q1: "Build something cool" },
  },
  { id: "end", type: "stage", title: "Curtain Call", subtitle: "Collect badges, revisit your pitch, and step back into the light." },
];

function lerp(a, b, n) { return (1 - n) * a + n * b; }

// small helper to convert hex -> [r,g,b]
function hexToRgb(hex) {
  const h = hex.replace("#", "");
  return [parseInt(h.substring(0, 2), 16), parseInt(h.substring(2, 4), 16), parseInt(h.substring(4, 6), 16)];
}

function confettiBurst(canvas) {
  if (!canvas) return;
  const ctx = canvas.getContext("2d");
  const W = (canvas.width = window.innerWidth);
  const H = (canvas.height = window.innerHeight);
  const pieces = [];
  for (let i = 0; i < 80; i++) pieces.push({ x: W/2, y: H/3, vx: (Math.random()-0.5)*12, vy: (Math.random()-1.5)*10, r: Math.random()*6+3, color: ["#ff6b6b","#ffd93d","#6bcB77","#6b8aff","#ff85e3"][Math.floor(Math.random()*5)], rot: Math.random()*360, vr: Math.random()*8-4 });
  let t = 0;
  function frame() {
    t++;
    ctx.clearRect(0,0,W,H);
    for (let p of pieces) {
      p.x += p.vx;
      p.y += p.vy + 0.3;
      p.vy += 0.06;
      ctx.save();
      ctx.translate(p.x, p.y);
      ctx.rotate((p.rot * Math.PI)/180);
      ctx.fillStyle = p.color;
      ctx.fillRect(-p.r/2, -p.r/2, p.r, p.r*0.6);
      ctx.restore();
    }
    if (t < 180) requestAnimationFrame(frame); else ctx.clearRect(0,0,W,H);
  }
  frame();
}

// --- VideoBackground: robust readiness + error callbacks ---
function VideoBackground({ src, videoRef, onReady, onError, keyOverride }) {
  const internalRef = useRef(null);
  const ref = videoRef || internalRef;

  useEffect(() => {
    const v = ref.current;
    if (!v) return;
    v.muted = true;
    v.playsInline = true;
    v.preload = "auto";
    // try to play (muted allows autoplay on most browsers)
    const playPromise = v.play && v.play();
    if (playPromise && playPromise.catch) playPromise.catch(() => { /* ignore */ });

    let fired = false;
    function markReady() {
      if (fired) return; fired = true; onReady && onReady();
    }
    function markError(err) {
      if (fired) return; fired = true; onError && onError(err);
    }

    function onCanPlay() { markReady(); }
    function onErrorFn(e) { console.warn("Video element error:", e); markError(e); }
    v.addEventListener("canplaythrough", onCanPlay);
    v.addEventListener("canplay", onCanPlay);
    v.addEventListener("loadeddata", onCanPlay);
    v.addEventListener("loadedmetadata", onCanPlay);
    v.addEventListener("error", onErrorFn);

    // allow tap to force-ready (useful on mobile)
    function onClick() { markReady(); }
    v.addEventListener("click", onClick);

    // Safety fallback
    const safety = setTimeout(() => { if (!fired) markError(new Error("video-safety-timeout")); }, 4500);

    return () => {
      v.removeEventListener("canplaythrough", onCanPlay);
      v.removeEventListener("canplay", onCanPlay);
      v.removeEventListener("loadeddata", onCanPlay);
      v.removeEventListener("loadedmetadata", onCanPlay);
      v.removeEventListener("error", onErrorFn);
      v.removeEventListener("click", onClick);
      clearTimeout(safety);
    };
  }, [src, onReady, onError, ref, keyOverride]);

  // video element sits behind everything; parent controls visibility
  return (
    <video
      key={keyOverride}
      ref={ref}
      src={src}
      muted
      loop
      playsInline
      autoPlay
      preload="auto"
      style={{ position: "fixed", inset: 0, width: "100%", height: "100%", objectFit: "cover", zIndex: -50, pointerEvents: "auto", opacity: 1 }}
    />
  );
}

