  const bgRAF = useRef(null);

  const activeIndexRef = useRef(0);
  const [activeIndex, setActiveIndex] = useState(0);

  // Loading state
  const [loadProgress, setLoadProgress] = useState(0);

  useEffect(() => {
    let raf = null; let start = performance.now(); let simulated = 0;
    function tick() {
      const now = performance.now();
      const elapsed = now - start;
      simulated = Math.min(92, simulated + (100 - simulated) * 0.06 + 0.8);
      setLoadProgress(prev => Math.min(92, Math.max(prev, simulated)));
      raf = requestAnimationFrame(tick);
    }
    raf = requestAnimationFrame(tick);
    return () => cancelAnimationFrame(raf);
  }, []);

  // Video callbacks
  function handleVideoReady() {
    setLoadProgress(100);
    setTimeout(() => {
      setOpeningDone(true);
      setVideoError(false);
    }, 280);
  }
  function handleVideoError(err) {
    console.warn("Video failed to play:", err);
    setVideoError(true);
    // allow UI to proceed (still show bricks + faint tint) but keep a visible retry affordance
    setTimeout(() => setLoadProgress(100), 300);
    setTimeout(() => setOpeningDone(true), 640);
  }

  // interactions: wheel/touch/pointer
  useEffect(() => {
    const isTouching = { v: false };
    const touchStartY = { v: 0 };

    function clampTarget(){ const max = Math.max(0, (visible.length - 1) * height); if (targetY.current < 0) targetY.current = 0; if (targetY.current > max) targetY.current = max; }

    function onWheel(e){ e.preventDefault(); targetY.current += e.deltaY * 1.2; clampTarget(); lastWheelAt.current = performance.now(); isScrolling.current = true; }
