          b.vy += (4 * norm) + (Math.random() * 2);
        }
      }
    };

    // palette from THEME
    const palA = hexToRgb(THEME.indigo);
    const palB = hexToRgb(THEME.cyan);
    const palC = hexToRgb(THEME.accent);

    let lastT = performance.now();
    function draw(now) {
      let dt = Math.min(40, now - lastT) * 0.001; if (dt <= 0) { bgRAF.current = requestAnimationFrame(draw); return; } lastT = now;
      ctx.clearRect(0,0,W,H);
      // translucent wash — see-through so video is visible
      ctx.fillStyle = openingDone ? "rgba(2,4,10,0.42)" : "rgba(2,4,10,0.08)";
      ctx.fillRect(0,0,W,H);

      const maxD = Math.max(W, H) * 0.65;
      const time = now * 0.001;
      const skipShadows = bricks.length > 700;

      for (let b of bricks) {
        if (b.gone) continue;
        if (b.falling) {
          b.fallVy += 900 * dt;
