        const bAB = mix(palA[2], palB[2], mixFactor);
        const accent = 0.08 * (0.5 + 0.5 * Math.sin(time * 1.8 + b.phase));
        const baseR = Math.min(255, Math.round(rAB * (1 - accent) + palC[0] * accent));
        const baseG = Math.min(255, Math.round(gAB * (1 - accent) + palC[1] * accent));
