  }

  function pulseScore() {
    const el = document.getElementById("score-badge");
    if (!el) return;
    el.animate([{ transform: "translateY(0) scale(1)" }, { transform: "translateY(-6px) scale(1.06)" }, { transform: "translateY(0) scale(1)" }], { duration: 520, easing: "ease-out" });
  }

  function onMiniGameComplete(success) {
    if (success) { setScore(s => s + 30); pulseScore(); }
    setAnswers(prev => ({ ...prev, q4: success ? "success" : "fail" }));
    setTimeout(() => scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)), MINIGAME_ADVANCE_MS);
  }

  function finish() { setShowFinish(true); if (canvasRef.current) confettiBurst(canvasRef.current); }

  function resetAll() { if (!window.confirm("Reset survey? This clears local progress.")) return; setAnswers({}); setScore(0); setShowFinish(false); targetY.current = 0; currentY.current = 0; if (containerRef.current) containerRef.current.style.transform = "translate3d(0,0,0)"; }

  function ChapterContent({ item, idx }) {
    if (item.type === "stage") {
      const isFirst = idx === 0;
      const isLast = idx === Math.max(0, visible.length - 1);
      return (
        <div className="text-center max-w-3xl mx-auto">
          <motion.h1 initial={{ y: 40, opacity: 0 }} animate={{ y: 0, opacity: 1 }} transition={{ duration: 0.8 }} className="text-5xl md:text-6xl font-extrabold tracking-tight" style={{ background: "linear-gradient(90deg,var(--indigo),var(--cyan))", WebkitBackgroundClip: "text", color: "transparent", textShadow: "0 6px 30px rgba(99,102,241,0.06)" }}>{item.title}</motion.h1>
          {item.subtitle && <motion.p initial={{ y: 20, opacity: 0 }} animate={{ y: 0, opacity: 0.95 }} transition={{ delay: 0.12 }} className="mt-4 text-lg" style={{ color: "var(--muted)" }}>{item.subtitle}</motion.p>}

          <div className="mt-8 mx-auto w-full max-w-md bg-[color:var(--panel)] border rounded-2xl p-6 shadow-xl" style={{ borderColor: "rgba(255,255,255,0.03)" }}>
            <div className="flex items-center gap-3">
              <svg width="40" height="40" viewBox="0 0 24 24" fill="none" className="opacity-95"><path d="M12 2l3 7h7l-5.5 4 2 7L12 17l-6.5 3 2-7L2 9h7l3-7z" stroke="var(--indigo)" strokeWidth="0.9"/></svg>
              <div className="text-left">
                <div className="font-semibold text-lg" style={{ color: "var(--text)" }}>Experience</div>
                <div className="text-sm" style={{ color: "var(--muted)" }}>A cinematic flow — read the prompt, pick quickly, and earn points.</div>
              </div>
            </div>

            <div className="mt-6">
              {isLast ? (
                <div className="flex gap-3 justify-center">
                  <button onClick={() => resetAll()} className="btn btn-primary">Restart</button>
                  <button onClick={() => setShowResponses(true)} className="btn btn-ghost">View responses</button>
                </div>
              ) : (
                <div className="flex gap-3 justify-center">
                  <button onClick={() => scrollToIndex(Math.min(idx + 1, visible.length - 1))} className="btn btn-primary">Enter</button>
                  {!isFirst && <button onClick={() => finish()} className="btn btn-ghost">Skip</button>}
                </div>
              )}
            </div>
          </div>
        </div>
      );
    }

    if (item.type === "mcq") {
      return (
        <div className="max-w-3xl w-full mx-auto">
          <div className="text-sm italic mb-4" style={{ color: "var(--muted)" }}>{item.flavor}</div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {item.choices.map(c => (
              <motion.button key={c} whileHover={{ scale: 1.03 }} onClick={() => handleAnswer(item.id, c)} className={`py-6 px-6 rounded-3xl border transform-gpu`} style={{
                borderColor: answers[item.id] === c ? "transparent" : "rgba(255,255,255,0.03)",
                background: answers[item.id] === c ? "linear-gradient(90deg,var(--indigo),var(--cyan))" : "rgba(255,255,255,0.02)",
                color: answers[item.id] === c ? "black" : "var(--text)",
                boxShadow: answers[item.id] === c ? "0 10px 40px rgba(99,102,241,0.14)" : "none"
              }}>
                <div className="font-semibold text-xl">{c}</div>
                <div className="text-sm mt-1" style={{ color: "var(--muted)" }}>{item.points && item.points[c] ? `${item.points[c]} pts` : ''}</div>
              </motion.button>
            ))}
          </div>
        </div>
      );
    }

    if (item.type === "rating") {
      return (
        <div className="text-center">
          <div className="text-sm italic mb-4" style={{ color: "var(--muted)" }}>{item.flavor}</div>
          <div className="flex items-center justify-center gap-4">
            {Array.from({ length: item.scale }).map((_, i) => (
              <motion.button key={i} onClick={() => handleAnswer(item.id, i+1)} whileTap={{ scale: 0.95 }} className="w-16 h-16 rounded-full flex items-center justify-center" style={{
                background: answers[item.id] === i+1 ? "linear-gradient(90deg,var(--indigo),var(--cyan))" : "rgba(255,255,255,0.02)",
                color: answers[item.id] === i+1 ? "black" : "var(--text)",
                border: "1px solid rgba(255,255,255,0.03)"
              }}>{i+1}</motion.button>
            ))}
          </div>
        </div>
      );
    }

    if (item.type === "text") {
      return (
        <div className="w-full max-w-3xl mx-auto">
          <div className="text-sm italic mb-4" style={{ color: "var(--muted)" }}>{item.flavor}</div>
          <textarea maxLength={item.maxLength} rows={5} value={answers[item.id] || ''} onChange={(e) => setAnswers(prev => ({ ...prev, [item.id]: e.target.value }))} className="w-full p-4 rounded-xl border bg-[color:var(--panel)]" style={{ borderColor: "rgba(255,255,255,0.03)", color: "var(--text)" }} placeholder="One-line pitch..." />
          <div className="text-xs mt-2" style={{ color: "var(--muted)" }}>{(answers[item.id] || '').length}/{item.maxLength}</div>
          <div className="mt-4 flex gap-4 justify-center">
            <button onClick={() => { if (item.points && (answers[item.id] || '').length > 0) { setScore(s => s + item.points); pulseScore(); } scrollToIndex(Math.min(activeIndexRef.current + 1, visible.length - 1)); }} className="btn btn-primary">Submit</button>
            <button onClick={() => setAnswers(prev => ({ ...prev, [item.id]: '' }))} className="btn btn-ghost">Clear</button>
          </div>
        </div>
      );
    }

    if (item.type === "minigame") return <MiniGame timeLimit={item.timeLimit} onComplete={onMiniGameComplete} flavor={item.flavor} />;
    return null;
  }

  // video src: choose fallback if requested
  const currentVideoSrc = useFallbackWhenError();

  function useFallbackWhenError() {
    // if user explicitly asked to use fallback, use it, otherwise attempt provided src first
    return useFallback ? REMOTE_FALLBACK : OPENING_VIDEO_SRC;
  }

  // retry video: bump key and reset flags
  function retryVideo(useRemoteFallback = false) {
    setVideoError(false);
    setUseFallback(useRemoteFallback);
    setVideoKey(k => k + 1);
    setOpeningDone(false);
    setLoadProgress(0);
  }

  // small decorative orbs for creative look
  const Orbs = () => (
    <>
      <div className="pointer-events-none fixed -left-24 -top-24 w-72 h-72 rounded-full blur-3xl opacity-30" style={{ background: "radial-gradient(circle at 30% 30%, var(--indigo), transparent)" }} />
      <div className="pointer-events-none fixed -right-28 top-1/3 w-96 h-96 rounded-full blur-3xl opacity-20" style={{ background: "radial-gradient(circle at 70% 70%, var(--accent), transparent)" }} />
    </>
  );

  return (
    <div className="min-h-screen relative overflow-hidden" style={{ background: THEME.background, color: THEME.text }}>
      {/* global CSS variables + small utility classes */}
      <style>{`
        :root{
          --indigo: ${THEME.indigo};
          --cyan: ${THEME.cyan};
          --accent: ${THEME.accent};
          --bg: ${THEME.background};
          --panel: ${THEME.panel};
          --muted: ${THEME.mutedText};
          --text: ${THEME.text};
        }
        .btn { padding: 0.6rem 1rem; border-radius: 0.625rem; font-weight:600; cursor:pointer; box-shadow: 0 6px 30px rgba(4,6,24,0.6); border: 1px solid rgba(255,255,255,0.035); }
        .btn-primary { background: linear-gradient(90deg,var(--indigo),var(--cyan)); color: black; }
        .btn-ghost { background: rgba(255,255,255,0.02); color: var(--text); border: 1px solid rgba(255,255,255,0.03); }
        .btn:active { transform: translateY(1px); }
        .glow { box-shadow: 0 8px 40px rgba(99,102,241,0.12); }
        .shimmer { background: linear-gradient(90deg, rgba(255,255,255,0.02), rgba(255,255,255,0.05), rgba(255,255,255,0.02)); background-size: 200% 100%; animation: shimmer 2.8s linear infinite; }
        @keyframes shimmer { 0% { background-position: 200% 0 } 100% { background-position: -200% 0 } }
        .panel-frost { background: linear-gradient(180deg, rgba(255,255,255,0.02), rgba(255,255,255,0.01)); border-radius: 12px; border: 1px solid rgba(255,255,255,0.03); }
      `}</style>

      <CustomCursor />

      {/* decorative orbs */}
      <Orbs />

      {/* VIDEO BACKGROUND */}
      <VideoBackground keyOverride={videoKey} src={currentVideoSrc} videoRef={videoRef} onReady={handleVideoReady} onError={handleVideoError} />

      {/* If video errored show overlay with retry affordances */}
      {videoError && (
        <div className="fixed inset-0 z-60 flex items-center justify-center p-6">
          <div className="bg-[color:var(--panel)] panel-frost p-6 rounded-xl text-center" style={{ width: "min(720px, 92%)" }}>
            <h3 style={{ color: "var(--text)", fontSize: "1.25rem", fontWeight: 700 }}>Video failed to play</h3>
            <p style={{ color: "var(--muted)", marginTop: 8 }}>This can happen if the file path is incorrect, the codec isn't supported by the browser, or CORS prevented loading.</p>
            <div className="mt-4 flex gap-3 justify-center">
              <button className="btn btn-primary" onClick={() => retryVideo(false)}>Retry local video</button>
              <button className="btn btn-ghost" onClick={() => retryVideo(true)}>Use fallback video</button>
            </div>
            <div className="mt-3 text-xs" style={{ color: "var(--muted)" }}>Tip: Confirm <code>public/opening.mp4</code> exists and is H.264/AAC for best browser support.</div>
          </div>
        </div>
      )}

      {/* Loading overlay while not opened yet */}
      {!openingDone && !videoError && <LoadingOverlay progress={loadProgress} />}

      {/* background bricks canvas + pop confetti canvas */}
      <canvas ref={bgCanvasRef} className="pointer-events-none fixed inset-0 z-0" />
      <canvas ref={canvasRef} className="pointer-events-none fixed inset-0 z-50" />

      {/* top-right score */}
      <div className="fixed right-6 top-6 z-60 flex flex-col gap-3 items-end">
        <div id="score-badge" className="px-4 py-2 rounded-full glow" style={{ background: "linear-gradient(90deg,var(--indigo),var(--accent))", color: "black", fontWeight: 700 }}>{score} pts</div>
        <div className="text-xs" style={{ color: "var(--muted)" }}>Level {Math.floor(score / 50) + 1}</div>
      </div>

      {/* left nav dots */}
      <nav className="fixed left-6 top-1/2 -translate-y-1/2 z-60 hidden md:flex flex-col gap-3">
        {visible.map((_, i) => (
          <button key={i} onClick={() => scrollToIndex(i)} className="rounded-full" aria-label={`Go to chapter ${i+1}`} style={{
            width: 12, height: 12,
            background: i === activeIndex ? "linear-gradient(90deg,var(--indigo),var(--accent))" : "rgba(255,255,255,0.03)",
            boxShadow: i === activeIndex ? "0 8px 30px rgba(99,102,241,0.12)" : "none",
            border: "1px solid rgba(255,255,255,0.02)"
          }} />
        ))}
      </nav>

      {/* main stacked chapters */}
      <div ref={containerRef} className="fixed inset-0 top-0 left-0 w-full will-change-transform z-40 cinema-stack">
        <div style={{ height: `${visible.length * height}px` }}>
          {visible.map((item, i) => (
            <section key={item.id} ref={addChapterRef} style={{ height: `${height}px` }} className="flex items-center justify-center px-6 md:px-12">
              <div className="w-full max-w-6xl mx-auto grid grid-cols-1 lg:grid-cols-3 gap-6 items-center chapter-node">
                <aside className="col-span-1 hidden lg:flex flex-col gap-4 items-start">
                  <div className="text-sm" style={{ color: "var(--muted)" }}>Chapter</div>
                  <motion.div animate={{ rotate: activeIndex === i ? 6 : 0 }} transition={{ type: "spring" }} className="text-4xl md:text-5xl font-extrabold" style={{ color: "var(--text)" }}>{String(i+1).padStart(2, "0")}</motion.div>
                  <div className="w-1 h-full rounded-full" style={{ background: "linear-gradient(180deg,var(--indigo),var(--accent))", opacity: 0.28 }} />
                </aside>
                <div className="col-span-2 rounded-xl p-8 text-center panel-frost">
                  <motion.div initial={{ opacity: 0, y: 30 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }}>
                    <ChapterContent item={item} idx={i} />
                  </motion.div>
                </div>
              </div>
            </section>
          ))}
        </div>
      </div>

      {/* finish modal */}
      {showFinish && (
        <div className="fixed inset-0 z-70 flex items-center justify-center">
          <div className="bg-[color:var(--panel)] panel-frost rounded-xl p-8 text-center" style={{ width: "min(680px, 92%)" }}>
            <h2 className="text-3xl font-bold" style={{ color: "var(--text)" }}>All done 🎉</h2>
            <p className="mt-3" style={{ color: "var(--muted)" }}>You earned <span style={{ fontWeight: 700, color: "var(--text)" }}>{score}</span> points. Thanks for playing!</p>
            <div className="mt-6 flex gap-3 justify-center"><button onClick={resetAll} className="btn btn-primary">Reset</button></div>
          </div>
        </div>
      )}

      {/* responses viewer */}
      <AnimatePresence>{showResponses && (
        <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-90 flex items-center justify-center p-6">
          <div className="bg-[color:var(--panel)] panel-frost rounded-xl p-6 max-w-3xl w-full">
            <div className="flex justify-between items-center">
              <h3 style={{ color: "var(--text)", fontWeight: 700 }}>Your Responses</h3>
              <button onClick={() => setShowResponses(false)} className="text-sm" style={{ color: "var(--muted)" }}>Close</button>
            </div>
            <div className="mt-4 responses-pre text-sm" style={{ color: "var(--text)", background: "rgba(0,0,0,0.25)", padding: 12, borderRadius: 8 }}>{JSON.stringify(answers, null, 2)}</div>
            <div className="mt-4 flex gap-3 justify-end">
              <button onClick={() => { setShowResponses(false); resetAll(); }} className="btn btn-primary">Restart</button>
            </div>
          </div>
        </motion.div>
      )}</AnimatePresence>
    </div>
  );
}
